    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    nume VARCHAR(150) NOT NULL,
    descriere TEXT NOT NULL,
    imagine VARCHAR(255),
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);









