CREATE DATABASE ghid_turistic;

USE ghid_turistic;

CREATE TABLE destinatii (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL,
    descriere TEXT,
    imagine VARCHAR(255) -- Stocăm URL-ul imaginii
);


CREATE TABLE detalii_destinatii (
    id INT AUTO_INCREMENT PRIMARY KEY,
    destinatie_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (destinatie_id) REFERENCES destinatii(id) ON DELETE CASCADE
);

# se pot pune infinite (sau nici chiar) imagini cu virgula intre ele sau cu update
# UPDATE detalii_destinatii 
# SET galerie_foto = CONCAT(galerie_foto, ',images/palat4.jpg') 
# WHERE destinatie_id = 1;

CREATE TABLE orase (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL, 
    descriere TEXT,
    imagine VARCHAR(255)
);


CREATE TABLE detalii_orase (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);



CREATE TABLE locuri_vizitat (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    nume VARCHAR(150) NOT NULL,
    descriere TEXT NOT NULL,
    imagine VARCHAR(255),
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);


CREATE TABLE hoteluri (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    nume VARCHAR(255) NOT NULL,
    adresa TEXT NOT NULL,
    numar_telefon VARCHAR(20) NOT NULL,
    stele INT CHECK (stele BETWEEN 1 AND 5),
    website VARCHAR(255),
    descriere_lunga TEXT NOT NULL,
    facilitati TEXT NOT NULL,
    camere TEXT NOT NULL,
    pret DECIMAL(10, 2) NOT NULL,
    imagine VARCHAR(255),
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);










CREATE TABLE orased (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL, 
    descriere TEXT,
    imagine VARCHAR(255)
);


CREATE TABLE detalii_orased (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES orased(id) ON DELETE CASCADE
);


CREATE TABLE locuri_vizitat_delta (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    nume VARCHAR(150) NOT NULL,
    descriere TEXT NOT NULL,
    imagine VARCHAR(255),
    FOREIGN KEY (orase_id) REFERENCES orased(id) ON DELETE CASCADE
);












CREATE TABLE statb (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL, 
    descriere TEXT,
    imagine VARCHAR(255)
);


CREATE TABLE detalii_statb (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES statb(id) ON DELETE CASCADE
);


CREATE TABLE locuri_vizitat_statb (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    nume VARCHAR(150) NOT NULL,
    descriere TEXT NOT NULL,
    imagine VARCHAR(255),
    FOREIGN KEY (orase_id) REFERENCES statb(id) ON DELETE CASCADE
);





















CREATE TABLE feedback (
  id int AUTO_INCREMENT PRIMARY KEY,
  name varchar(20) NOT NULL,
  email varchar(30) NOT NULL,
  feedbk varchar(1000) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE table auth(
  id int AUTO_INCREMENT PRIMARY KEY,
  username varchar(100) UNIQUE NOT NULL,
  parola varchar(100) NOT NULL
)

