INSERT INTO orased (nume, descriere, imagine) VALUES
('Braila', 'Brăila este un municipiu din sud-estul României, situat pe malul stâng al Dunării. Este unul dintre cele mai vechi orașe din țară, menționat documentar încă din 1368. Orașul are un important trecut comercial și portuar, fiind un nod economic strategic în perioada modernă.', 'images/braila.jpg'),
('Calarasi', 'Călărași este un oraș situat în sud-estul României, la granița cu Bulgaria, pe malul Dunării. Orașul este renumit pentru frumusețea sa naturală, cu peisaje pitorești și zone verzi generoase, dar și pentru atmosfera sa liniștită și prietenoasă.', 'images/calarasi.jpg'),
('Chilia Veche', 'Chilia Veche este o localitate din Delta Dunării, situată pe brațul Chilia al Dunării, aproape de granița cu Ucraina. Cu o istorie veche, a fost un important centru comercial și strategic. Astăzi, atrage turiști prin peisajele sălbatice, biodiversitatea unică și tradițiile pescărești autentice.', 'images/chilia.jpg'),
('Drobeta-Turnu Severin', 'Drobeta-Turnu Severin este un oraș situat în sud-vestul României, în regiunea Oltenia, la confluența râurilor Dunărea și Jiu. Orașul este renumit pentru Monumentul Tabula Traiana, un important simbol al istoriei romane, dar și pentru Parcul Natural Porțile de Fier, o rezervație naturală protejată.', 'images/drobeta.jpg'),
('Galati', 'Galați este un important municipiu din estul României, situat pe malul Dunării. Este un centru industrial și portuar major, având unul dintre cele mai mari șantiere navale din țară și un important combinat siderurgic.','images/galati.jpg'),
('Giurgiu', 'Giurgiu este un oraș situat în sudul României, la granița cu Bulgaria, pe malul Dunării. Orașul este renumit pentru Podul Prieteniei, un important simbol al relațiilor româno-bulgare, dar și pentru Cetatea Giurgiu, un monument istoric semnificativ.', 'images/giurgiu.jpg'),
('Mila 23', 'Mila 23 este un sat pescăresc din Delta Dunării, situat pe brațul Sulina. Este locul natal al celebrului canotor Ivan Patzaichin. Cunoscut pentru tradițiile lipovenești, peisajele spectaculoase și accesul ușor la canalele și lacurile deltei, Mila 23 atrage turiști pasionați de natură și pescuit.','images/mila23.jpg'),
('Orsova', 'Orșova este un oraș situat în sud-vestul României, pe malul Dunării, în județul Mehedinți. Este cunoscut pentru peisajele spectaculoase ale Clisurii Dunării și pentru apropierea de Cazanele Dunării, una dintre cele mai frumoase zone naturale din țară.','images/orsova.jpg'),
('Sfantu Gheorghe', 'Sfântu Gheorghe este un sat din Delta Dunării, situat pe brațul cu același nume. Este cunoscut pentru peisajele naturale deosebite, biodiversitatea bogată și tradițiile locale. Aici se află și o comunitate de pescari care practică metode tradiționale de pescuit.', 'images/sfantu_gheorghe.jpg'),
('Sulina', 'Sulina este un oraș din județul Tulcea, situat în extremitatea estică a României, la vărsarea Dunării în Marea Neagră. Fost port comercial și sediu al Comisiei Europene a Dunării, Sulina are un patrimoniu multicultural, plaje sălbatice și un peisaj unic în Delta Dunării, atrăgând turiști și pescari.', 'images/sulina.jpg'),
('Tulcea', 'Tulcea este un oraș situat în estul României, la confluența râurilor Dunărea și Sfântu Gheorghe, fiind un important centru cultural și economic al regiunii Dobrogea. Orașul este renumit pentru Delta Dunării, unul dintre cele mai mari și mai frumoase ecosisteme din Europa, dar și pentru Monumentul Independenței.', 'images/tulcea.jpg');
INSERT INTO detalii_orased (orase_id, descriere_lunga, galerie_foto) VALUES
(1,'Cu o istorie bogată, ce datează din Evul Mediu, orașul a fost un important centru comercial și portuar, având un rol strategic datorită poziției sale pe Dunăre. Brăila este un oraș cu o istorie fascinantă, o economie în dezvoltare și un patrimoniu cultural remarcabil. Deși nu mai are aceeași importanță economică de altădată, continuă să fie un centru regional important, atrăgând turiști prin arhitectura sa deosebită, evenimentele culturale și peisajele pitorești de pe malul Dunării.', 'images/braila1.jpg, images/braila2.jpg, images/braila3.jpg, images/braila4.jpg'),
(2,'Călărași este un oraș cu o istorie îndelungată, ce datează din perioada medievală, fiind un important centru comercial și portuar. Situat pe malul Dunării, orașul oferă o combinație interesantă de istorie, cultură și natură. Călărași este renumit pentru frumusețea sa naturală, cu peisaje pitorești, dar și pentru atmosfera sa liniștită și prietenoasă. Orașul este un loc ideal pentru relaxare și explorare, oferind oportunități pentru turism, sporturi nautice și agrement.', 'images/calarasi1.jpg, images/calarasi2.jpg, images/calarasi3.jpg, images/calarasi4.jpg'),
(3,'Chilia Veche este un sat cu o istorie bogată, ce datează din perioada romană, fiind un important centru comercial și portuar. Situat pe brațul Chilia al Dunării, aproape de granița cu Ucraina, satul oferă o combinație interesantă de istorie, cultură și natură. Chilia Veche este renumită pentru frumusețea sa naturală, cu peisaje pitorești, dar și pentru atmosfera sa liniștită și prietenoasă. Satul este un loc ideal pentru relaxare și explorare, oferind oportunități pentru turism, sporturi nautice și agrement.', 'images/chilia1.jpg, images/chilia2.jpg, images/chilia3.jpg, images/chilia4.jpg'),
(4,'Drobeta-Turnu Severin este un oraș cu o istorie bogată, ce datează din perioada romană, fiind un important centru strategic și comercial. Situat la confluența râurilor Dunărea și Jiu, orașul oferă o combinație interesantă de istorie, cultură și natură. Drobeta-Turnu Severin este renumit pentru Monumentul Tabula Traiana, un important simbol al istoriei romane, dar și pentru Parcul Natural Porțile de Fier, o rezervație naturală protejată. Orașul este un loc ideal pentru explorare, relaxare și turism, oferind oportunități pentru drumeții, sporturi nautice și agrement.', 'images/drobeta1.jpg, images/drobeta2.jpg, images/drobeta3.jpg, images/drobeta4.jpg'),
(5,'Galați este un oraș cu o istorie îndelungată, ce datează din perioada medievală, fiind un important centru comercial și portuar. Situat pe malul Dunării, orașul oferă o combinație interesantă de istorie, cultură și natură. Galați este renumit pentru frumusețea sa naturală, cu peisaje pitorești, dar și pentru atmosfera sa liniștită și prietenoasă. Orașul este un loc ideal pentru relaxare și explorare, oferind oportunități pentru turism, sporturi nautice și agrement.', 'images/galati1.jpg, images/galati2.jpg, images/galati3.jpg, images/galati4.jpg'),
(6,'Giurgiu este un oraș cu o istorie îndelungată, ce datează din perioada medievală, fiind un important centru comercial și portuar. Situat pe malul Dunării, orașul oferă o combinație interesantă de istorie, cultură și natură. Giurgiu este renumit pentru frumusețea sa naturală, cu peisaje pitorești, dar și pentru atmosfera sa liniștită și prietenoasă. Orașul este un loc ideal pentru relaxare și explorare, oferind oportunități pentru turism, sporturi nautice și agrement.', 'images/giurgiu1.jpg, images/giurgiu2.jpg, images/giurgiu3.jpg, images/giurgiu4.jpg'),
(7,'Mila 23 este un sat cu o istorie bogată, ce datează din perioada romană, fiind un important centru comercial și portuar. Situat pe brațul Sulina al Dunării, aproape de granița cu Ucraina, satul oferă o combinație interesantă de istorie, cultură și natură. Mila 23 este renumită pentru frumusețea sa naturală, cu peisaje pitorești, dar și pentru atmosfera sa liniștită și prietenoasă. Satul este un loc ideal pentru relaxare și explorare, oferind oportunități pentru turism, sporturi nautice și agrement.', 'images/mila23_1.jpg, images/mila23_2.jpg, images/mila23_3.jpg, images/mila23_4.jpg'),
(8,'Orșova este un oraș cu o istorie bogată, ce datează din perioada romană, fiind un important centru strategic și comercial. Situat la confluența râurilor Dunărea și Cerna, orașul oferă o combinație interesantă de istorie, cultură și natură. Orșova este renumit pentru peisajele spectaculoase ale Clisurii Dunării și pentru apropierea de Cazanele Dunării, una dintre cele mai frumoase zone naturale din țară. Orașul este un loc ideal pentru explorare, relaxare și turism, oferind o gamă variată de activități.', 'images/orsova1.jpg, images/orsova2.jpg, images/orsova3.jpg, images/orsova4.jpg'),
(9,'Sfântu Gheorghe este un sat cu o istorie bogată, ce datează din perioada romană, fiind un important centru comercial și portuar. Situat pe brațul cu același nume al Dunării, aproape de granița cu Ucraina, satul oferă o combinație interesantă de istorie, cultură și natură. Sfântu Gheorghe este renumit pentru frumusețea sa naturală, cu peisaje pitorești, dar și pentru atmosfera sa liniștită și prietenoasă. Satul este un loc ideal pentru relaxare și explorare, oferind oportunități pentru turism, sporturi nautice și agrement.', 'images/sfantu_gheorghe1.jpg, images/sfantu_gheorghe2.jpg, images/sfantu_gheorghe3.jpg, images/sfantu_gheorghe4.jpg'),
(10,'Sulina este un oraș din județul Tulcea, situat la vărsarea Dunării în Marea Neagră. Izolat de restul țării, accesibil doar pe apă, orașul are o istorie bogată, fiind sediul Comisiei Europene a Dunării în secolul XIX. Sulina se remarcă prin farul istoric, cimitirul multicultural și plajele sălbatice, neaglomerate. Natura din jur oferă peisaje spectaculoase, fiind parte din Rezervația Biosferei Delta Dunării. Pescuitul și gastronomia locală atrag turiști, iar atmosfera liniștită face din Sulina o destinație ideală pentru relaxare, explorare și descoperirea unui colț unic al României, unde Dunărea întâlnește marea.', 'images/sulina1.jpg, images/sulina2.jpg, images/sulina3.jpg, images/sulina4.jpg'),
(11,'Tulcea este un oraș cu o istorie îndelungată, ce datează din perioada romană, fiind un important centru comercial și portuar. Situat la confluența râurilor Dunărea și Sfântu Gheorghe, orașul oferă o combinație interesantă de istorie, cultură și natură. Tulcea este renumit pentru Delta Dunării, unul dintre cele mai mari și mai frumoase ecosisteme din Europa, dar și pentru Monumentul Independenței, un important simbol al istoriei românești. Orașul este un punct de plecare ideal pentru explorarea Deltei Dunării.', 'images/tulcea1.jpg, images/tulcea2.jpg, images/tulcea3.jpg, images/tulcea4.jpg');
INSERT INTO locuri_vizitat_delta (orase_id, nume, descriere, imagine) VALUES
(1,'Faleza Dunarii','Faleza Dunării din Brăila este una dintre cele mai frumoase zone de promenadă din oraș, oferind priveliști spectaculoase asupra fluviului. Aici, localnicii și turiștii se pot plimba pe aleile umbrite de copaci, pot admira navele care traversează Dunărea și se pot relaxa pe bănci sau terase. Zona este ideală pentru jogging, plimbări cu bicicleta și evenimente culturale în aer liber. De asemenea, faleza găzduiește un port turistic și pontoane pentru ambarcațiuni de agrement. La apus, peisajul devine deosebit de pitoresc, atrăgând numeroși fotografi și iubitori ai naturii.','images/faleza.jpg'),
(1,'Parcul Monument','Parcul Monument din Brăila este cel mai mare spațiu verde al orașului, ideal pentru relaxare, sport și activități în aer liber. Amenajat în secolul al XIX-lea, parcul impresionează prin aleile largi, arborii seculari și zonele de agrement. Aici se află un stadion, piste pentru biciclete și terenuri de sport. Parcul găzduiește evenimente culturale și festivaluri, devenind un loc popular pentru comunitate. În timpul verii, este o oază de răcoare, iar toamna, frunzișul colorat oferă un peisaj spectaculos. Este locul perfect pentru plimbări, socializare sau pur și simplu pentru a te bucura de natură.','images/parc.jpg'),
(1,'Biserica Greaca','Biserica Greacă din Brăila este un monument istoric important, situat în centrul orașului. Construită în stil neoclasic la începutul secolului al XIX-lea, aceasta reprezenta locul de cult pentru comunitatea grecească din Brăila, care a avut o influență semnificativă în dezvoltarea economică și culturală a orașului. Biserica se remarcă prin arhitectura sa impunătoare, cu detalii fine și un interior decorat cu icoane și fresce deosebite. De-a lungul decadelor, biserica a fost un centru spiritual al grecilor din regiune și un simbol al diversității culturale a Brăilei.','images/biserica_greaca.jpg'),
(1,'Teatrul "Maria Filotti"','Teatrul „Maria Filotti” din Brăila este un important centru cultural al orașului, cunoscut pentru activitatea sa artistică de lungă durată. Construit în 1889, clădirea teatrului se remarcă prin arhitectura sa elegantă, în stil neoclasic, și oferă un cadru perfect pentru spectacole de teatru, operă, balet și concerte. Numele teatrului provine de la celebra actriță Maria Filotti, o figură emblematică a teatrului românesc. De-a lungul timpului, teatrul a găzduit producții remarcabile, atrăgând public din întreaga țară. Astăzi, continuă să fie un loc de referință pentru iubitorii de cultură și artă din Brăila.','images/centru_vechi.jpg'),
(1,'Cetatea Brăilei','Cetatea Brăilei este un important obiectiv istoric al orașului, având o istorie de peste 600 de ani. Atestată documentar în 1368, cetatea a avut un rol strategic în apărarea regiunii și a fost un punct cheie în evoluția orașului. De-a lungul timpului, cetatea a fost fortificată și reconstruită, dar în prezent, din vechile structuri mai pot fi observate doar ruine. Acestea oferă o privire asupra trecutului glorios al orașului și a importanței sale în perioada medievală. Zona cetății este o destinație turistică, fiind înconjurată de un peisaj pitoresc și istorie fascinantă.','images/cetatea_brailei.jpg'),
(2,'Parcul Central','Parcul Central din Călărași este un loc de relaxare și recreere situat în centrul orașului, fiind un spațiu verde popular printre localnici și vizitatori. Amenajat cu alei îngrijite, bănci, și vegetație diversificată, parcul oferă un cadru plăcut pentru plimbări și activități în aer liber. Un lac mic, populat cu rațe și alte păsări, adaugă un farmec aparte zonei. Este locul ideal pentru familii, copii, dar și pentru cei care doresc să se bucure de o pauză liniștită. Parcul Central din Călărași este un simbol al orașului, un loc unde natura și viața urbană se întâlnesc armonios.','images/parc_central.jpg'),
(2,'Biserica Sf. Nicolae','Biserica „Sfântul Nicolae” din Călărași este un monument religios important, cu o istorie ce datează din secolul XIX. Construită în stil arhitectural tradițional românesc, biserica impresionează prin simplitatea și frumusețea sa. Interiorul este decorat cu icoane și fresce, iar atmosfera este una de liniște și spiritualitate. Biserica este un loc de cult activ, unde enoriașii se adună pentru slujbe și sărbători religioase. De-a lungul timpului, „Sfântul Nicolae” a fost un simbol al comunității ortodoxe din Călărași, fiind un loc de adăpost și rugăciune pentru credincioși.','images/biserica_sf_nicolae.jpg'),
(2,'Muzeul Dunarii','Muzeul Dunării din Călărași este o instituție culturală dedicată istoriei și tradițiilor regiunii dunărene. Aici sunt expuse colecții de obiecte ce ilustrează viața pe malul fluviului, de la evoluția navigației și pescuitului, până la obiceiuri și meșteșuguri tradiționale. Muzeul oferă o privire detaliată asupra impactului Dunării asupra economiei și culturii locale, dar și asupra diversității naturale a regiunii. Vizitatorii pot explora expoziții permanente și temporare, iar muzeul joacă un rol important în conservarea patrimoniului istoric și cultural al zonei. Este un loc fascinant pentru iubitorii de istorie și natură.','images/muzeu_dunare.jpg'),
(2,'Lacul Valea Roșie','Lacul Valea Roșie, situat în apropierea comunei Mitreni din județul Călărași, este un loc pitorești cunoscut pentru proprietățile sale curative. Apa sărată și nămolul terapeutic din lac sunt folosite în scopuri medicinale, atrăgând vizitatori care doresc să beneficieze de efectele acestora asupra sănătății. Lacul este, de asemenea, un loc preferat pentru pescuit și activități recreative, fiind înconjurat de un peisaj natural deosebit. Zona oferă un refugiu liniștit, ideal pentru cei care vor să se relaxeze sau să se bucure de natura autentică din regiune.','images/lacul_valea.jpg'),
(2,'Bratul Borcea','Bratul Borcea este o ramură a fluviului Dunărea, situată în județul Călărași, care oferă peisaje spectaculoase și un ecosistem divers. Acesta este un loc popular pentru excursii cu vaporașul, pescuit și observarea faunei locale, incluzând specii de păsări și pești. Bratul Borcea formează un cadru natural ideal pentru relaxare, fiind adesea vizitat de iubitorii de natură și turiștii care doresc să se bucure de liniștea regiunii. Cu ape limpezi și un mediu unic, bratul este o destinație perfectă pentru cei ce doresc să exploreze frumusețea Deltei Dunării.','images/bratul_borcea.jpg'),
(3,'Pădurea Letea','Pădurea Letea, situată în nordul Deltei Dunării, este cea mai veche rezervație naturală din România și un loc unic în Europa. Cunoscută pentru combinația rară de pădure subtropicală și dune de nisip, aceasta găzduiește stejari seculari, liane ca în junglele tropicale și o faună diversă. Cele mai faimoase atracții sunt caii sălbatici, care trăiesc în libertate și oferă un spectacol impresionant vizitatorilor. Pădurea poate fi explorată prin tururi ghidate cu jeepuri sau căruțe, oferind o experiență fascinantă în inima naturii neatinse. Este un loc ideal pentru iubitorii de aventură și biodiversitate.','images/padurea_letea.jpg'),
(3,'Biserica Ortodoxă Sfântul Mihail și Gavril','Biserica Ortodoxă „Sfântul Mihail și Gavril” este un important lăcaș de cult din Chilia Veche, având o semnificație deosebită pentru comunitatea locală. Construită în stil tradițional, biserica impresionează prin arhitectura sa impunătoare și detaliile ornamentale specifice. Interiorul este decorat cu fresce religioase deosebite, care redau scene biblice și chipurile sfinților. Lăcașul de cult este un centru spiritual esențial pentru locuitorii din zonă, găzduind slujbe și evenimente religioase importante. Pelerinii și turiștii care vizitează biserica descoperă nu doar un loc de rugăciune, ci și un simbol al tradiției și credinței ortodoxe.','images/biserica_ortodoxa.jpg'),
(3,'Ruinele Cetatii Chilia','Ruinele Cetății Chilia sunt rămășițele unei fortificații medievale situate pe malul Dunării, în apropierea localității Chilia Veche. Cetatea a fost un important centru strategic și comercial în Evul Mediu, disputat de moldoveni, otomani și genovezi. Se crede că a fost ridicată în secolul al XIV-lea, sub domnia lui Alexandru cel Bun, pentru a proteja rutele comerciale dunărene. De-a lungul timpului, cetatea a fost distrusă și reconstruită de mai multe ori. Astăzi, ruinele sale oferă o incursiune fascinantă în istorie, atrăgând turiști pasionați de vestigii medievale și peisaje spectaculoase.','images/ruinele_cetatii.jpg'),
(3,'Lacul Matița-Merhei','Lacul Matița-Merhei este o zonă lacustră impresionantă situată în Delta Dunării, renumită pentru biodiversitatea sa spectaculoasă. Format dintr-un complex de lacuri, canale și stufărișuri, acest habitat natural adăpostește numeroase specii de păsări, inclusiv pelicani, egrete și lebede. Apele sale sunt bogate în pește, atrăgând pescari și iubitori ai naturii. Peisajele pitorești și liniștea oferă o experiență de neuitat pentru turiștii care explorează zona cu bărci sau canoe. Lacul Matița-Merhei este un loc ideal pentru fotografii spectaculoase și pentru observarea faunei sălbatice, făcând parte din patrimoniul natural al Deltei Dunării.','images/lacul_matita.jpg'),
(3,'Insula K','Insula K este o mică insulă situată în Delta Dunării, faimoasă pentru frumusețea naturală și biodiversitatea sa. Accesibilă doar pe apă, insula oferă peisaje spectaculoase și este un loc popular pentru iubitorii de natură și observatorii de păsări. Aici se găsesc numeroase specii de păsări, inclusiv pelicani și egrete, iar flora diversă adăpostește vegetație specifică zonei umede. Insula K este o destinație ideală pentru excursii în natură, oferind o atmosferă liniștită și izolată, perfectă pentru relaxare și fotografii de neuitat. Este un loc perfect pentru cei care doresc să exploreze frumusețea sălbatică a Deltei Dunării.','images/insula_k.jpg'),
(4,'Cetatea Drobeta','Cetatea Drobeta este un important sit arheologic situat în apropierea orașului Drobeta-Turnu Severin, pe malul Dunării, în județul Mehedinți. Construită în perioada romană, în timpul împăratului Traian, cetatea a avut rol defensiv și strategic, protejând trecerea pe malul sudic al Dunării. Ruinele cetății sunt impresionante, incluzând vestigii ale zidurilor de fortificație, turnurilor și porților. Drobeta a fost un important oraș roman, iar cetatea face parte dintr-un complex istoric semnificativ, care include și Podul lui Apollodor, aflat în apropiere. Astăzi, ruinele atrag turiști și pasionați de istorie, oferind o fereastră în trecutul roman al regiunii.','images/cetate_drobeta.jpg'),
(4,'Parcul Natural Porțile de Fier','Parcul Natural Porțile de Fier este o arie protejată situată pe malul sudic al Dunării, în județele Mehedinți și Caraș-Severin, ce cuprinde o diversitate naturală impresionantă. Zona este cunoscută pentru peisajele spectaculoase formate de munți, stânci și canioane, dar și pentru biodiversitatea sa. Parcul adăpostește numeroase specii de floră și faună, inclusiv specii rare de păsări și plante. Atracții importante din parc includ Cheile Dunării și Lacul de Acumulare Porțile de Fier, dar și vestigii istorice, cum ar fi cetăți și fortificații romane. Este o destinație populară pentru iubitorii de natură, drumeții și fotografie.','images/parc_pozile_de_fier.jpg'),
(4,'Cetatea Medievală a Severinului','Cetatea Medievală a Severinului, situată în Drobeta-Turnu Severin, datează din secolul al XIV-lea și a avut un rol strategic important în perioada medievală. Construită pentru a proteja trecerea Dunării și a controla comerțul, cetatea a fost extinsă și consolidată de-a lungul decadelor, fiind un punct militar cheie. Astăzi, ruinele cetății sunt o atracție turistică semnificativă, iar vizitatorii pot explora zidurile fortificate și turnurile, admirând peisajul din jur. Cetatea oferă o privire asupra istoriei regiunii și este un loc ideal pentru iubitorii de istorie și arhitectură medievală.','images/cetatea_severinului.jpg'),
(4,'Palatul Culturii „Teodor Costescu”','Palatul Culturii „Teodor Costescu” din Drobeta-Turnu Severin este un important centru cultural al orașului, un loc unde se organizează evenimente, spectacole și expoziții. Clădirea impresionantă, construită în stil neoclasic, poartă numele unui mare artist local, Teodor Costescu. Palatul găzduiește activități culturale diverse, fiind un simbol al educației și culturii în Mehedinți. De asemenea, aceasta adăpostește instituții precum Filarmonica „Ion Dumitrescu” și Muzeul de Artă, oferind vizitatorilor o oportunitate de a învăța și de a se bucura de cultura locală. Este un loc ideal pentru cei interesați de artă și evenimente culturale.','images/palat_cultura.jpg'),
(4,'Podul lui Traian','Podul lui Traian este un simbol al ingineriei romane, situat în apropiere de Drobeta-Turnu Severin, peste Dunăre. Construit în 105 d.Hr. sub conducerea împăratului Traian, acest pod a fost una dintre cele mai mari realizări ingineresti ale antichității, având scopul de a facilita armatei romane traversarea Dunării în timpul războaielor cu dacii. Podul măsura aproximativ 1.135 metri lungime și 15 metri înălțime, fiind considerat o minune arhitecturală a epocii. Astăzi, doar ruinele acestuia mai pot fi văzute, însă el rămâne o atracție istorică importantă, simbolizând puterea și realizările Imperiului Roman.','images/pod_traian.jpg'),
(5,'Gradina Botanica','Grădina Botanică din Galați este un loc deosebit dedicat iubitorilor de natură, aflat în apropierea centrului orașului. Aici, vizitatorii pot explora o colecție variată de plante din diverse colțuri ale lumii, incluzând specii autohtone și exotice. Grădina este un loc ideal pentru relaxare, plimbări liniștite sau sesiuni de fotografie, oferind o atmosferă calmă și revigorantă. Printre atracțiile sale se numără alei umbroase, lacuri cu nuferi și sere care adăpostesc plante tropicale. Grădina Botanică este un spațiu educativ, dar și un refugiu natural în mijlocul orașului.','images/gradina_botanica.jpg'),
(5,'Muzeul de Istorie','Muzeul de Istorie din Galați este un loc important pentru înțelegerea evoluției regiunii și a orașului de-a lungul timpului. Aflat în centrul orașului, muzeul găzduiește colecții valoroase de artefacte arheologice, etnografice și istorice, care ilustrează istoria antică, medievală și modernă a Galațiului și a zonei înconjurătoare. Printre exponate se numără obiecte din perioada romană, artefacte medievale și piese din timpul Războiului de Independență. Muzeul este un loc ideal pentru pasionații de istorie și pentru cei care doresc să descopere moștenirea culturală a regiunii.','images/muzeu_istorie.jpg'),
(5,'Biserica Sf. Apostol Andrei','Biserica Sf. Apostol Andrei din Galați este un loc de cult important, construită în stil neoclasic, cu influențe bizantine. Aceasta a fost ridicată în secolul al XIX-lea și reprezintă unul dintre cele mai vechi lăcașuri de cult ale orașului. Interiorul bisericii este decorat cu icoane și fresce deosebite, iar arhitectura sa impunătoare atrage vizitatori și credincioși deopotrivă. Biserica este dedicată Sf. Apostol Andrei, patronul spiritual al României, și este un loc de rugăciune și reculegere. Ea joacă un rol important în viața religioasă a comunității din Galați.','images/biserica_sf_apostol.jpg'),
(5,'Faleza Dunarii','Faleza Dunării din Galați este un loc de promenadă emblematic pentru oraș, oferind un peisaj spectaculos asupra fluviului și a zonei înconjurătoare. Amenajată pe malul Dunării, faleza este ideală pentru plimbări, relaxare sau sporturi de apă. Pe lângă priveliștile impresionante, vizitatorii pot găsi cafenele, terase și locuri de joacă pentru copii. De-a lungul falezei se află și statui, monumente și zone verzi, care adaugă un plus de farmec zonei. Faleza este un loc de întâlnire popular atât pentru localnici, cât și pentru turiști, fiind o destinație de neuitat în Galați.','images/faleza_dunarii.jpg'),
(5,'Turnul de Televiziune','Turnul de Televiziune din Galați este un simbol al orașului, construit în anii 1970. Cu o înălțime de 120 de metri, este unul dintre cele mai înalte puncte de observație din regiune. Turnul oferă o panoramă spectaculoasă asupra Galațiului și a zonei înconjurătoare, inclusiv a Dunării. În partea superioară, există o platformă de observație, accesibilă vizitatorilor, care le permite să admire peisajul orașului și să fotografieze zona. Acesta este un loc popular pentru turiști și localnici deopotrivă, fiind un punct de referință în peisajul urban al Galațiului.','images/turn_televiziune.jpg'),
(6,'Podul Prieteniei','Podul Prieteniei Giurgiu-Ruse este un simbol al legăturii dintre România și Bulgaria, fiind primul pod peste Dunăre care unește cele două țări. Construit între 1952 și 1954, are o lungime de aproximativ 2,8 km și include atât o cale ferată, cât și o șosea. Podul reprezintă un punct strategic important pentru transport și comerț între Europa Centrală și Balcani. Pe lângă importanța economică, oferă și o panoramă spectaculoasă asupra Dunării. Cu o arhitectură solidă, Podul Prieteniei rămâne una dintre cele mai importante infrastructuri transfrontaliere din sud-estul Europei.','images/pod_prieteniei.jpg'),
(6,'Cetatea Giurgiu','Cetatea Giurgiu, ridicată în secolul al XIV-lea de către domnitorii Țării Românești, a avut un rol strategic în apărarea Dunării și a fost scena unor numeroase confruntări între otomani și valahi. Construită pe o insulă a Dunării, cetatea a fost fortificată de-a lungul timpului, devenind un punct-cheie în apărarea regiunii. De-a lungul secolelor, a fost distrusă și reconstruită de mai multe ori, ajungând în prezent în stare de ruină. Totuși, rămășițele sale reprezintă un important vestigiu istoric, oferind vizitatorilor ocazia de a descoperi trecutul medieval al Giurgiului.','images/cetate_giurgiu.jpg'),
(6,'Turnul cu Ceas','Turnul cu Ceas din Giurgiu este un simbol istoric al orașului, construit în perioada otomană, probabil în secolul al XVIII-lea. Situat în centrul vechi, turnul avea inițial rol de supraveghere și apărare, fiind ulterior transformat într-un turn cu ceas pentru a marca timpul locuitorilor. De-a lungul anilor, a suferit multiple restaurări, păstrându-și însă farmecul arhitectural oriental. Astăzi, rămâne un punct de atracție pentru turiști, amintind de influențele otomane asupra orașului. Turnul cu Ceas este un simbol al continuității istorice și al identității culturale a Giurgiului.','images/turn_ceas.jpg'),
(6,'Muzeul Județean Teohari Antonescu','Muzeul Județean „Teohari Antonescu” din Giurgiu este un important centru cultural și istoric, găzduind colecții ce reflectă trecutul regiunii. Înființat în 1934, muzeul poartă numele istoricului Teohari Antonescu și prezintă expoziții de arheologie, etnografie și istorie locală. Printre exponate se regăsesc artefacte din perioada daco-romană, documente medievale, obiecte tradiționale și mărturii ale dezvoltării orașului Giurgiu. Situat într-o clădire elegantă din secolul al XIX-lea, muzeul oferă vizitatorilor o incursiune captivantă în istoria și cultura zonei, fiind un obiectiv turistic de neratat pentru pasionații de istorie.','images/muzeu_teohari.jpg'),
(6,'Parcul Mihai Viteazul','Parcul Mihai Viteazul din Giurgiu este unul dintre cele mai importante spații verzi ale orașului, oferind locuitorilor și turiștilor un loc ideal pentru relaxare și recreere. Situat în centrul orașului, parcul este amenajat cu alei pitorești, spații verzi bine întreținute și bănci unde vizitatorii se pot bucura de liniște. Unul dintre punctele de atracție este statuia ecvestră a lui Mihai Viteazul, care simbolizează curajul și unitatea națională. Parcul este frecventat atât de familii cu copii, cât și de iubitorii de natură, fiind un loc perfect pentru plimbări și activități în aer liber.','images/parc_mihai_viteazul.jpg'),
(7,'Lacul Mila 23','Lacul Mila 23, situat în inima Deltei Dunării, este o adevărată oază naturală, ideală pentru pescuit, explorare și observarea faunei sălbatice. Înconjurat de stuf, sălcii și nuferi, lacul găzduiește numeroase specii de păsări, inclusiv pelicani, egrete și cormorani. Apa sa limpede atrage pescarii în căutare de știucă, somn și crap. Zona este accesibilă doar pe apă, oferind o experiență autentică a Deltei. Lacul este un punct de plecare pentru excursii cu barca pe canalele din jur, fiind apreciat de turiștii care doresc să se bucure de liniștea și frumusețea naturii sălbatice.','images/lacul_mila_23.jpg'),
(7,'Lacul Razim','Lacul Razim, cel mai mare lac din România, face parte din complexul lagunar Razim-Sinoe și este situat în apropierea Deltei Dunării. Separat de Marea Neagră printr-un cordon de nisip, lacul are ape liniștite, fiind un habitat important pentru păsări precum pelicani și lebede. Este o destinație ideală pentru pescuit, observarea faunei și excursii cu barca. Pe malurile sale se află vestigii istorice, precum cetatea Enisala. Lacul oferă peisaje spectaculoase, mai ales la apus, atrăgând iubitori de natură și fotografie. Este un loc perfect pentru relaxare și explorarea biodiversității Deltei.','images/lacul_razim.jpg'),
(7,'Biserica Lipoveneasca','Biserica Lipovenească din Mila 23 este un simbol al comunității de ruși lipoveni din Deltă, păstrătoare a tradițiilor ortodoxe de rit vechi. Construită din lemn, biserica impresionează prin simplitatea sa arhitecturală și atmosfera liniștită. Interiorul este decorat cu icoane pictate manual și elemente specifice ortodoxiei ruse. Aici, slujbele sunt oficiate în limba slavonă, iar sărbătorile religioase sunt marcate cu ritualuri autentice. Biserica reprezintă un punct de atracție pentru turiștii care doresc să descopere cultura lipovenilor și spiritualitatea profundă a acestei comunități tradiționale din Delta Dunării.','images/biserica_lipoveneasca.jpg'),
(7,'Schitului Sfântul Antonie de la Stipoc','Schitul Sfântul Antonie de la Stipoc este singura mănăstire ortodoxă din inima Deltei Dunării, situată pe grindul Stipoc, între Mila 23 și Chilia Veche. Înființat în 2003, schitul oferă un loc de reculegere pentru pelerini și turiști. Construit din lemn, în stil tradițional, lăcașul este înconjurat de peisaje sălbatice, specifice Deltei. Călugării duc o viață simplă, bazată pe rugăciune și muncă, având grijă de gospodăria proprie. Accesibil doar pe apă, schitul este un loc de liniște și spiritualitate, atrăgând vizitatori dornici să descopere credința și frumusețea Deltei.','images/schitul_sf_anton.jpg'),
(7,'Muzeul Ivan Patzaichin','Muzeul Ivan Patzaichin – Centru de Inovare Comunitară din Mila 23 este dedicat celebrului canoist Ivan Patzaichin și culturii locale. Inaugurat în 2024, muzeul cuprinde patru pavilioane, inclusiv un observator de 18 metri care oferă o panoramă spectaculoasă asupra Deltei Dunării. Expozițiile prezintă viața sportivului și tradițiile lipovenești. Construit din materiale naturale, muzeul promovează sustenabilitatea și inovația comunitară. Accesul este gratuit, dar vizitarea se face pe bază de programare. Este un loc unic pentru descoperirea istoriei, naturii și tradițiilor din inima Deltei Dunării.','images/muzeu_ivan.jpg'),
(8,'Cazanele Dunarii','Cazanele Dunării sunt una dintre cele mai spectaculoase atracții naturale din România, situate în zona Parcului Natural Porțile de Fier. Aici, fluviul Dunărea străbate Munții Carpați, formând un defileu impresionant cu pereți stâncoși abrupți. Zona este renumită pentru Peștera Ponicova, Mănăstirea Mraconia și faimoasa sculptură a lui Decebal, cea mai mare sculptură în stâncă din Europa. Cazanele Mari și Cazanele Mici oferă peisaje deosebite, ideale pentru croaziere și drumeții. Cu o biodiversitate bogată și o istorie fascinantă, acest loc atrage turiști pasionați de natură, aventură și fotografie.','images/cazanele_dunarii.jpg'),
(8,'Manastirea Mraconia','Mănăstirea Mraconia, situată pe malul Dunării, în apropierea Cazanelor Mici, este un lăcaș de cult cu o istorie impresionantă. Construită inițial în secolul al XV-lea, mănăstirea a fost distrusă de mai multe ori și reconstruită în anii ’90 pe locul vechii așezări scufundate odată cu amenajarea hidrocentralei Porțile de Fier. Cu o arhitectură elegantă și o poziție pitorească, mănăstirea oferă o priveliște spectaculoasă asupra Dunării. Este un loc de reculegere și rugăciune, dar și un important obiectiv turistic pentru cei care vizitează Defileul Dunării și zona Cazanelor.','images/manastirea_mraconia.jpg'),
(8,'Mănăstirea Sfânta Ana','','images/manastirea_sf_ana.jpg'),
(8,'Cascada Bigăr','Cascada Bigăr, situată în Parcul Național Cheile Nerei-Beușnița, a fost una dintre cele mai spectaculoase cascade din România, cunoscută pentru forma sa unică și pentru apa care curgea uniform peste un con de tuf calcaros acoperit cu mușchi verde. A devenit faimoasă la nivel internațional după ce a fost inclusă într-un top al celor mai frumoase cascade din lume. Din păcate, în 2021, cascada s-a prăbușit din cauze naturale. Chiar și așa, zona rămâne o atracție turistică importantă, oferind peisaje spectaculoase și trasee de drumeție prin pădurile Munților Aninei.','images/cascada_bigar.jpg'),
(8,'Cheile Nerei','Cheile Nerei, situate în apropierea orașului Orșova, sunt o atracție naturală impresionantă din județul Mehedinți, făcând parte din Parcul Național Porțile de Fier. Râul Nera străbate un defileu strâmt, formând pereți stâncoși și peisaje spectaculoase. Zona este cunoscută pentru biodiversitatea sa bogată, cascadele pitorești și lacurile cristaline. Printre atracțiile celebre se numără Cascada Beușnița și diverse formațiuni geologice. Cheile Nerei sunt un loc ideal pentru drumeții, fotografie și explorare, fiind o destinație populară pentru iubitorii de natură care doresc să descopere frumusețea autentică a Deltei Dunării.','images/cheile_nerei.jpg'),
(9,'Muzeul Delta Dunării','Muzeul Delta Dunării, situat în localitatea Sfântu Gheorghe, este o destinație importantă pentru cei care doresc să descopere istoria, cultura și biodiversitatea Deltei Dunării. Muzeul expune o colecție variată de exponate, incluzând obiecte tradiționale, unelte de pescuit și articole din viața cotidiană a locuitorilor din Delta Dunării. De asemenea, sunt prezentate diverse specii de faună și floră specifice regiunii. Vizitatorii pot învăța despre formarea Deltei, ecosistemul său unic și importanța acesteia ca rezervație naturală globală. Muzeul este un loc perfect pentru cei care vor să înțeleagă mai bine acest colț de rai al României.','images/muzeul_delta.jpg'),
(9,'Biserica veche din Sfântu Gheorghe','Biserica veche din Sfântu Gheorghe este un lăcaș de cult tradițional, datând din secolul al XIX-lea, și reprezintă unul dintre simbolurile istorice ale localității. Aceasta este construită din cărămidă și lemn, cu un design simplu, dar impresionant. Biserica are un interior decorat cu icoane pe lemn și fresce care reflectă stilul ortodox tradițional. Este un loc de cult activ pentru comunitatea locală, având o atmosferă de liniște și spiritualitate. Vizitatorii pot învăța despre tradițiile religioase și culturale ale locuitorilor din Sfântu Gheorghe, fiind un loc important de închinare și istorie.','images/biserica_sf_gheorghe.jpg'),
(9,'Rezervația Sacalin-Zătoane','Rezervația Sacalin-Zătoane, situată în județul Tulcea, face parte din complexul ecologic al Deltei Dunării și este un important habitat natural pentru numeroase specii de faună și floră. Aceasta se întinde pe o suprafață de aproximativ 2.400 de hectare și include lacuri, canale, păduri și zone de mlaștină. Rezervația este cunoscută pentru diversitatea sa de păsări, fiind un loc important pentru migrarea și cuibărirea acestora, inclusiv pelicani și stilturi. De asemenea, este un refugiu pentru specii de plante rare și pești, fiind protejată pentru a menține echilibrul ecologic al regiunii. Este un loc ideal pentru ecoturism și observarea naturii.','images/rezervatia_sacalin.jpg'),
(9,'Pădurea Caraorman','Pădurea Caraorman, situată în apropierea localității Sfântu Gheorghe, în Delta Dunării, este o rezervație naturală remarcabilă. Aceasta este una dintre cele mai mari păduri de salcie albă din regiune, fiind un habitat important pentru multe specii de păsări, mamifere și plante rare. Pădurea se întinde pe o suprafață de aproximativ 500 de hectare și reprezintă un loc de refugiu pentru fauna sălbatică. Este un loc ideal pentru ecoturism și drumeții, oferind turiștilor ocazia de a explora un peisaj intact și autentic, departe de agitația urbană. Pădurea Caraorman este un simbol al biodiversității Deltei Dunării.','images/padurea_caraorman'),
(9,'Farul Sfântu Gheorghe','Farul Sfântu Gheorghe este un simbol al orașului și al regiunii Delta Dunării, situat la capătul canalului Sfântu Gheorghe, acolo unde fluviul Dunărea se varsă în Marea Neagră. Construit în secolul al XIX-lea, farul a fost un punct de referință important pentru navigația pe Dunăre, ghidând navele care se îndreptau spre portul Sulina. De-a lungul decadelor, acesta a suferit mai multe renovări, iar astăzi, farul este un obiectiv turistic important. Vizitatorii pot ajunge în zonă cu barca și pot explora peisajul impresionant al Deltei Dunării, înconjurat de vegetație sălbatică și ape liniștite.','images/farul_sf_gheorghe'),
(10,'Plaja Sulina','Plaja Sulina este una dintre cele mai frumoase și liniștite plaje de pe litoralul românesc, situată în apropierea orașului Sulina, la vărsarea Dunării în Marea Neagră. Accesibilă doar pe apă, aceasta oferă un peisaj natural spectaculos, cu nisip fin și ape curate. Plaja nu este aglomerată, fiind preferată de turiștii care caută relaxare și natură sălbatică. În apropiere, pot fi admirate dune de nisip și păsări specifice Deltei Dunării. Apa mării are o adâncime mică la mal, ceea ce o face ideală pentru familii cu copii. Plaja Sulina oferă o experiență autentică, departe de agitația stațiunilor turistice.','images/plaja_sulina.jpg'),
(10,'Farul Sulina','Farul Sulina, un simbol al orașului Sulina, este un monument istoric ce datează din secolul al XIX-lea. Construit inițial de otomani și extins de autoritățile europene, farul a ghidat navele pe Dunăre timp de decenii. Astăzi, este transformat în muzeu și oferă vizitatorilor informații despre istoria maritimă a zonei și activitatea Comisiei Europene a Dunării. Interiorul găzduiește expoziții despre viața navigatorilor și dezvoltarea portului Sulina. De pe platforma superioară, turiștii pot admira o panoramă spectaculoasă asupra orașului și Deltei Dunării. Farul Sulina este o atracție de neratat pentru pasionații de istorie și explorare.','images/farul_sulina.jpg'),
(10,'Palatul Comisiei Europene a Dunării','Palatul Comisiei Europene a Dunării, situat în orașul Sulina, a fost construit la sfârșitul secolului al XIX-lea și a servit ca sediu al Comisiei Europene a Dunării. Aceasta organizație internațională a avut rolul de a reglementa navigația pe Dunăre și de a asigura navigabilitatea fluviului, un aspect crucial pentru comerțul din regiune. Palatul este un exemplu remarcabil de arhitectură în stil neoclasic, cu influențe ale stilului european din perioada respectivă. Astăzi, clădirea găzduiește un muzeu dedicat istoriei și activității Comisiei Europene a Dunării și reprezintă un important punct de atracție turistică în Sulina.','images/palat_comisia.jpg'),
(10,'Cimitirul Maritim','Cimitirul Maritim din Sulina este un loc de odihnă pentru marinarii și navigatorii care au pierit pe Dunăre și în Marea Neagră de-a lungul decadelor. Așezat într-o zonă liniștită, cimitirul este un loc plin de istorie, având morminte ale unor marinari din diverse colțuri ale lumii, inclusiv din România, Grecia, Italia și alte țări europene. Multe dintre pietrele funerare sunt vechi și poartă inscripții în diferite limbi. Cimitirul oferă o atmosferă de reculegere și respect, fiind o atracție turistică importantă, mai ales pentru iubitorii de istorie maritimă și pentru cei interesați de tradițiile navigației pe Dunăre.','images/cimitir_maritim.jpg'),
(10,'Muzeul de Istorie și Etnografie','Muzeul de Istorie și Etnografie din Sulina este un loc unde vizitatorii pot descoperi istoria orașului și a regiunii Deltei Dunării, precum și tradițiile și obiceiurile comunităților locale. Expozițiile muzeului sunt variate, incluzând obiecte de patrimoniu, unelte de pescuit tradiționale, costume populare și fotografii istorice care ilustrează viața cotidiană a locuitorilor din Delta Dunării. De asemenea, muzeul prezintă artefacte legate de navigație și activitățile comerciale desfășurate în portul Sulina. Este un loc ideal pentru cei care doresc să înțeleagă mai bine cultura și istoria acestui colț de lume.','images/mudeul_de_etnografie.jpg'),
(11,'Parcul Natural Delta Dunarii','Parcul Natural Delta Dunării, situat în județul Tulcea, este una dintre cele mai importante și diverse rezervații naturale din Europa și face parte din Patrimoniul Mondial UNESCO. Acesta cuprinde o vastă rețea de canale, lacuri, păduri și zone umede, fiind un habitat unic pentru numeroase specii de plante și animale, inclusiv multe specii rare de păsări migratoare. Delta Dunării este un loc ideal pentru ecoturism, oferind posibilități de explorare cu barca, observație a păsărilor, pescuit și drumeții. Parcul are o biodiversitate extraordinară, fiind casă pentru aproape 300 de specii de păsări și multe specii de pești și plante. De asemenea, regiunea este importantă din punct de vedere cultural, având comunități tradiționale care păstrează obiceiuri și meșteșuguri ancestrale. Delta Dunării este un loc deosebit de important pentru iubitorii de natură, fiind o destinație perfectă pentru relaxare și explorare.','images/parc_delta_dunarii.jpg'),
(11,'Cetatea Adamclisi','Cetatea Adamclisi, situată în județul Tulcea, este un important sit arheologic din perioada romană, cunoscută pentru Trofeul lui Traian. Construită în secolul al II-lea d.Hr., aceasta marchează victoria împăratului Traian în războaiele cu dacii. Monumentul principal al cetății este Trofeul lui Traian, un obelisc mare, decorat cu reliefuri care ilustrează diverse scene din bătăliile cu dacii. Cetatea a avut rolul de fort militar, dar și de simbol al triumfului roman. Astăzi, cetatea este un obiectiv turistic major, cu ruinele bine conservate și muzeul adiacent, care oferă vizitatorilor o privire detaliată asupra istoriei și culturii romane din această perioadă.','images/cetate_adamclisi.jpg'),
(11,'Cetatea Enisala','','images/cetate_enisala.jpg'),
(11,'Biserica Sf. Nicolae','Biserica „Sfântul Nicolae” din Tulcea este un lăcaș de cult vechi și important din oraș, având o istorie care datează din secolul al XIX-lea. Construită în stil neoclasic, biserica impresionează prin arhitectura sa simplă, dar elegantă. Ea a fost ridicată de către comunitatea locală pentru a adăposti credincioșii din zona respectivă. Interiorul bisericii este decorat cu icoane și fresce ce reflectă tradițiile religioase ale regiunii. Biserica „Sfântul Nicolae” este un loc de cult activ, fiind un important punct de spiritualitate și cultură pentru locuitorii din Tulcea. Este, de asemenea, un obiectiv turistic, admirat pentru frumusețea și liniștea pe care le oferă vizitatorilor.','images/biserica_sf_nicolae.jpg'),
(11,'Muzeul de Istorie si Arheologie','Muzeul de Istorie și Arheologie din Tulcea este un important obiectiv cultural ce adăpostește colecții valoroase ce ilustrează istoria regiunii Dobrogea și a Deltei Dunării. Muzeul include expoziții permanente care acoperă diverse perioade istorice, de la preistorie, civilizațiile grecești și romane, până la perioada medievală și modernă. Printre cele mai valoroase exponate se numără artefacte descoperite în situri arheologice din zonă, precum și obiecte din epoca dacică și romană. Una dintre cele mai remarcabile expoziții este legată de istoria Deltei Dunării, cu exponate ce reflectă evoluția regiunii și viața localnicilor. De asemenea, muzeul are o colecție de piese de ceramică, unelte de muncă și obiecte de cult, care oferă o perspectivă asupra tradițiilor și obiceiurilor locale. Muzeul este un loc ideal pentru cei interesați de istoria și arheologia regiunii Tulcea și Dobrogea.','images/muzeu_arheologie.jpg');
INSERT INTO statb (nume, descriere, imagine) VALUES
('Amara','Băile Amara, situat în județul Ialomița, este o stațiune balneoclimaterică renumită pentru lacul sărat Amara și nămolul sapropelic, utilizat în tratamente reumatologice și dermatologice. Orașul oferă relaxare, terapii naturale și peisaje liniștitoare, atrăgând turiști pentru sănătate și recreere.', 'images/amara.jpg'),
('Baile Calacea','Băile Călacea, situate în județul Timiș, sunt renumite pentru apele termale cu proprietăți terapeutice, benefice pentru afecțiuni reumatologice și neurologice. Stațiunea oferă băi termale, spații de relaxare și un parc natural, fiind o destinație ideală pentru tratament și recreere.', 'images/calacea.jpg'),
('Baile Felix','Băile Felix, situate lângă Oradea, sunt cea mai mare stațiune balneară din România, renumită pentru apele termale cu proprietăți terapeutice. Oferă piscine, centre spa și tratamente pentru afecțiuni reumatologice. Stațiunea atrage turiști pentru relaxare, wellness și peisajele sale pitorești, fiind deschisă tot timpul anului.', 'images/felix.jpg'),
('Baile Govora','Băile Govora, situate în județul Vâlcea, sunt renumite pentru apele minerale cu efecte terapeutice asupra afecțiunilor respiratorii și reumatismale. Stațiunea oferă aer curat, peisaje liniștite și centre de tratament, fiind o destinație ideală pentru relaxare și recuperare medicală.', 'images/govora.jpg'),
('Baile Herculane','Băile Herculane, situate în Valea Cernei, sunt cea mai veche stațiune balneară din România, cu origini romane. Renumite pentru apele termale cu proprietăți curative, atrag turiști pentru tratamente reumatologice, peisaje spectaculoase și istorie. Oferă relaxare, drumeții și explorarea vestigiilor antice.', 'images/herculane.jpg'),
('Baile Homorod','Băile Homorod, situate în județul Harghita, sunt o stațiune balneoclimaterică renumită pentru izvoarele minerale cu efecte benefice asupra sistemului digestiv și circulator. Oferă aer curat, peisaje pitorești și oportunități de relaxare, fiind o destinație ideală pentru odihnă și tratamente naturale.','images/homorod.jpg'),
('Baile Olanesti','Băile Olănești, situate în județul Vâlcea, sunt renumite pentru izvoarele minerale cu proprietăți curative, folosite în tratarea afecțiunilor renale, digestive și reumatologice. Stațiunea oferă aer curat, peisaje montane și centre de tratament, fiind o destinație ideală pentru sănătate și relaxare.', 'images/olanesti.jpg'),
('Baile Tusnad','Băile Tușnad, situate în Carpații Orientali, sunt cea mai mică stațiune balneară din România, renumită pentru apele minerale și mofetele cu efecte benefice asupra sistemului cardiovascular. Oferă aer curat, peisaje montane și activități recreative precum drumeții și băi termale, fiind o destinație ideală pentru relaxare și tratament.', 'images/tusnad.jpg'),
('Balvanyos','Balvanyos, situată în județul Covasna, este o stațiune balneoclimaterică renumită pentru mofetele sale naturale și izvoarele minerale cu efecte benefice asupra circulației sanguine. Înconjurată de peisaje montane spectaculoase, oferă acces la Lacul Sfânta Ana și Tinovul Mohoș, fiind o destinație ideală pentru relaxare și tratamente naturale.','images/balvanyos.jpg'),
('Borsec','Borsec, situat în Carpații Orientali, este o stațiune balneară renumită pentru izvoarele sale de apă minerală cu efecte terapeutice. Oferă aer curat, peisaje montane și activități recreative precum drumeții, schi și relaxare în natură. Apa minerală Borsec este apreciată atât în România, cât și internațional.', 'images/borsec.jpg'),
('Calimanesti-Caciulata','Călimănești-Căciulata, situată pe Valea Oltului, este una dintre cele mai cunoscute stațiuni balneare din România, renumită pentru apele termale și peisajele montane spectaculoase. Oferă tratamente balneare, relaxare și atracții precum Mănăstirea Cozia și Parcul Național Cozia, fiind o destinație ideală pentru sănătate și turism.', 'images/calimanesti.jpg'),
('Lacu Rosu','Lacu Roșu, situat în Carpații Orientali, este un lac de baraj natural unic, format prin prăbușirea unui versant. Este înconjurat de peisaje spectaculoase și păduri de conifere, oferind activități precum drumeții, plimbări cu barca și explorarea Cheilor Bicazului. Destinație ideală pentru iubitorii de natură și aventură.', 'images/rosu.jpg'),
('Lacu Sarat','Lacu Sărat, situat lângă Brăila, este o stațiune balneară renumită pentru apa sărată și nămolul terapeutic, benefice în tratarea afecțiunilor reumatologice și dermatologice. Oferă baze de tratament, plajă și zone de relaxare, fiind o destinație ideală pentru sănătate și recreere.', 'images/sarat.jpg'),
('Ocna Sibiului','Ocna Sibiului, situată lângă Sibiu, este o stațiune balneară renumită pentru lacurile sărate formate în foste mine de sare, cu proprietăți terapeutice benefice pentru afecțiuni reumatologice și dermatologice. Oferă băi naturale, peisaje pitorești și relaxare, fiind supranumită „Litoralul Ardealului”.', 'images/ocna.jpg'),
('Ocna Sugatag','Ocna Șugatag, situată în Maramureș, este o stațiune balneară renumită pentru apele sărate provenite din foste mine de sare, benefice în tratarea afecțiunilor reumatologice și neurologice. Oferă băi termale, peisaje pitorești și acces la tradițiile autentice maramureșene, fiind o destinație ideală pentru relaxare și tratament.','images/sugatag.jpg'),
('Praid','Praid, situat în județul Harghita, este renumit pentru Salina Praid, una dintre cele mai mari din Europa, cu beneficii terapeutice pentru afecțiuni respiratorii. Stațiunea oferă și băi sărate, trasee de drumeție și peisaje pitorești, fiind o destinație ideală pentru relaxare, tratament și explorarea tradițiilor secuiești.','images/praid.jpg'),
('Slanic Moldova','Slănic Moldova, situată în Carpații Orientali, este o stațiune balneară renumită pentru izvoarele minerale cu proprietăți terapeutice. Oferă tratamente pentru diverse afecțiuni, aer curat, peisaje montane și activități recreative precum drumeții și sporturi de iarnă, fiind o destinație ideală pentru sănătate și relaxare.','images/slanic.jpg'),
('Sovata','Sovata, situată în inima Transilvaniei, este o stațiune balneară renumită pentru Lacul Ursu, un lac helioterm cu proprietăți terapeutice unice. Apele sărate și nămolul sunt benefice pentru afecțiuni reumatologice și ginecologice. Stațiunea oferă peisaje pitorești, trasee de drumeție și oportunități de relaxare pe tot parcursul anului.', 'images/sovata.jpg');
INSERT INTO detalii_statb (orase_id, descriere_lunga, galerie_foto) VALUES
(1,'Amara, situată în județul Ialomița, este o destinație balneară cunoscută pentru efectele benefice ale lacului sărat și ale nămolului sapropelic. Stațiunea atrage vizitatori datorită proprietăților curative ale apei, utilizate în terapii pentru afecțiuni ale pielii și ale sistemului locomotor. Pe lângă tratamentele balneare, Amara oferă un cadru relaxant, cu aer curat și peisaje liniștite, ideale pentru odihnă și refacere. De asemenea, orașul găzduiește diverse evenimente culturale și activități de agrement, transformându-l într-o alegere potrivită atât pentru cei care caută relaxare, cât și pentru cei interesați de turismul de sănătate.','images/amara1.jpg, images/amara2.jpg, images/amara3.jpg, images/amara4.jpg'),
(2,'Băile Călacea, aflate în județul Timiș, sunt o stațiune balneară cunoscută pentru apele termale cu efecte benefice asupra sistemului locomotor și nervos. Datorită compoziției minerale, aceste ape sunt utilizate în tratamente pentru afecțiuni reumatologice și neurologice. Stațiunea oferă băi termale moderne, spații de relaxare și un parc natural ce completează atmosfera de liniște și refacere. Climatul blând și peisajele verzi atrag turiști atât pentru tratamente, cât și pentru recreere. Băile Călacea reprezintă o alegere ideală pentru cei care caută sănătate, odihnă și echilibru natural.','images/calacea1.jpg, images/calacea2.jpg, images/calacea3.jpg, images/calacea4.jpg'),
(3,'Băile Felix, aflate în apropierea orașului Oradea, sunt o stațiune balneară apreciată pentru izvoarele termale cu efecte curative. Apele bogate în minerale sunt utilizate în tratarea afecțiunilor reumatice, neurologice și în programe de recuperare. Stațiunea dispune de baze de tratament moderne, piscine termale și centre de wellness, oferind o experiență de relaxare pe tot parcursul anului. Climatul blând și peisajele verzi completează atmosfera de refacere. Turiștii se pot bucura atât de terapii naturale, cât și de activități recreative, făcând din Băile Felix o destinație ideală pentru sănătate și relaxare.','images/felix1.jpg, images/felix2.jpg, images/felix3.jpg, images/felix4.jpg'),
(4,'Băile Govora, aflate în județul Vâlcea, sunt o stațiune balneară apreciată pentru apele minerale bogate în iod, brom și sulf, recunoscute pentru beneficiile lor în tratarea afecțiunilor respiratorii și locomotorii. Climatul tonic, aerul ozonat și peisajele naturale oferă un cadru ideal pentru refacere și relaxare. Stațiunea dispune de baze moderne de tratament, unde se aplică proceduri balneare eficiente. Pe lângă terapii, vizitatorii se pot bucura de plimbări prin parcuri amenajate și de liniștea specifică zonei, făcând din Băile Govora o destinație ideală pentru sănătate și recreere.','images/govora1.jpg, images/govora2.jpg, images/govora3.jpg, images/govora4.jpg'),
(5,'Băile Herculane, situate în pitoreasca Vale a Cernei, sunt una dintre cele mai vechi stațiuni balneare din Europa, având origini care datează din perioada romană. Cunoscută pentru apele sale termale sulfuroase, cu efecte benefice asupra sistemului locomotor și circulator, stațiunea atrage turiști în căutare de tratamente naturale și relaxare. Pe lângă terapiile balneare, vizitatorii se pot bucura de trasee montane spectaculoase, aer curat și vestigii istorice, cum ar fi băile romane și statuia lui Hercules. Băile Herculane rămân o destinație unică, îmbinând sănătatea, natura și istoria într-un cadru impresionant.','images/herculane1.jpg, images/herculane2.jpg, images/herculane3.jpg, images/herculane4.jpg'),
(6,'Băile Homorod, situate în județul Harghita, sunt o stațiune balneoclimaterică apreciată pentru izvoarele minerale bogate în minerale, utilizate în tratarea afecțiunilor digestive și circulatorii. Zona se remarcă prin aerul curat, climatul liniștit și peisajele montane care oferă un cadru perfect pentru relaxare și refacere. Stațiunea dispune de facilități pentru tratamente balneare și recreere, atrăgând turiști în căutare de terapii naturale și odihnă activă. Băile Homorod sunt o alegere ideală pentru cei care doresc să se bucure de beneficiile naturii într-un mediu relaxant și reconfortant.','images/homorod1.jpg, images/homorod2.jpg, images/homorod3.jpg, images/homorod4.jpg'),
(7,'Băile Olănești, situate în județul Vâlcea, sunt o stațiune balneară apreciată pentru numeroasele sale izvoare minerale cu efecte terapeutice. Apele sunt recunoscute pentru beneficiile lor în tratarea afecțiunilor renale, digestive, reumatologice și dermatologice. Stațiunea oferă un cadru natural deosebit, cu aer curat și peisaje montane, ideale pentru relaxare și refacere. În plus, dispune de centre moderne de tratament și wellness, atrăgând turiști atât pentru terapii balneare, cât și pentru recreere. Băile Olănești rămân o destinație populară pentru cei care caută sănătate, liniște și natură.','images/olanesti1.jpg, images/olanesti2.jpg, images/olanesti3.jpg, images/olanesti4.jpg'),
(8,'Băile Tușnad, situate în inima Carpaților Orientali, sunt una dintre cele mai pitorești stațiuni balneare din România, apreciate pentru apele minerale și mofetele cu efecte terapeutice asupra sistemului cardiovascular și nervos. Climatul montan, aerul ozonat și peisajele spectaculoase creează un cadru perfect pentru relaxare și refacere. Stațiunea oferă baze de tratament moderne, băi termale și numeroase oportunități de recreere, precum drumeții și plimbări în natură. Datorită atmosferei sale liniștite și beneficiilor terapeutice, Băile Tușnad sunt o destinație ideală pentru cei care caută sănătate și odihnă.','images/tusnad1.jpg, images/tusnad2.jpg, images/tusnad3.jpg, images/tusnad4.jpg'),
(9,'Balvanyos, aflată în județul Covasna, este o stațiune balneoclimaterică apreciată pentru mofetele naturale și izvoarele minerale cu proprietăți terapeutice asupra circulației sanguine și sistemului respirator. Amplasată într-un cadru montan deosebit, stațiunea oferă aer curat, trasee turistice și oportunități de relaxare în mijlocul naturii. Datorită proximității față de Lacul Sfânta Ana și Tinovul Mohoș, Balvanyos atrage atât turiști în căutare de tratamente naturale, cât și iubitori de aventură și peisaje spectaculoase. Combinând beneficiile balneare cu frumusețea peisajului, stațiunea este o alegere ideală pentru sănătate și recreere.','images/balvanyos1.jpg, images/balvanyos2.jpg, images/balvanyos3.jpg, images/balvanyos4.jpg'),
(10,'Borsec, situat în inima Carpaților Orientali, este una dintre cele mai cunoscute stațiuni balneare din România, celebră pentru izvoarele sale de apă minerală cu proprietăți terapeutice. Apele de aici sunt apreciate pentru beneficiile asupra sistemului digestiv și renal, fiind recunoscute la nivel internațional. Stațiunea oferă un climat tonic, aer curat și peisaje montane spectaculoase, ideale pentru relaxare și refacere. În plus, turiștii pot practica diverse activități recreative, precum drumeții, schi și explorarea traseelor naturale. Borsec rămâne o destinație de top pentru sănătate, natură și aventură.','images/borsec1.jpg, images/borsec2.jpg, images/borsec3.jpg, images/borsec4.jpg'),
(11,'Călimănești-Căciulata, situată pe pitoreasca Vale a Oltului, este una dintre cele mai apreciate stațiuni balneare din România, recunoscută pentru apele sale termale cu proprietăți curative. Stațiunea oferă baze moderne de tratament, piscine cu ape minerale și un cadru natural ideal pentru relaxare și refacere. În plus, atracțiile din zonă, precum Mănăstirea Cozia și Parcul Național Cozia, completează experiența turiștilor prin oportunități de explorare culturală și drumeții. Datorită combinației dintre beneficiile terapeutice, peisajele spectaculoase și oferta diversificată de activități, Călimănești-Căciulata rămâne o destinație de top pentru sănătate și turism.','images/calimanesti1.jpg, images/calimanesti2.jpg, images/calimanesti3.jpg, images/calimanesti4.jpg'),
(12,'Lacu Roșu, aflat în inima Carpaților Orientali, este un lac de baraj natural spectaculos, format în urma unei alunecări de teren. Înconjurat de păduri de conifere și stânci impunătoare, oferă un peisaj deosebit și un refugiu perfect pentru iubitorii de natură. Zona este ideală pentru drumeții, plimbări cu barca și explorarea impresionantelor Chei ale Bicazului. Datorită atmosferei sale liniștite și frumuseții naturale, Lacu Roșu atrage turiști în căutare de aventură, relaxare și peisaje montane deosebite, fiind una dintre cele mai pitorești destinații din România.','images/rosu1.jpg, images/rosu2.jpg, images/rosu3.jpg, images/rosu4.jpg'),
(13,'Lacu Sărat, aflat în apropierea Brăilei, este o stațiune balneară cunoscută pentru apa sărată și nămolul sapropelic cu proprietăți terapeutice. Aceste resurse naturale sunt utilizate în tratamente pentru afecțiuni reumatologice, dermatologice și ale sistemului locomotor. Stațiunea dispune de baze moderne de tratament, zone de relaxare și o plajă amenajată, oferind o combinație perfectă între terapie și recreere. Datorită climatului favorabil și efectelor benefice ale apei și nămolului, Lacu Sărat atrage anual turiști în căutare de sănătate și odihnă într-un cadru natural liniștit.','images/sarat1.jpg, images/sarat2.jpg, images/sarat3.jpg, images/sarat4.jpg'),
(14,'Ocna Sibiului, situată în apropierea Sibiului, este o stațiune balneară faimoasă pentru lacurile sărate formate în foste saline prăbușite. Apele acestor lacuri au o salinitate ridicată și sunt recunoscute pentru efectele lor terapeutice asupra afecțiunilor reumatologice, dermatologice și ale sistemului locomotor. Stațiunea oferă băi naturale, peisaje pitorești și oportunități de relaxare, atrăgând turiști în căutare de tratamente naturale și recreere. Datorită proprietăților unice ale apei și nămolului, Ocna Sibiului este cunoscută și sub denumirea de „Litoralul Ardealului”, fiind o destinație ideală pentru sănătate și odihnă.','images/ocna1.jpg, images/ocna2.jpg, images/ocna3.jpg, images/ocna4.jpg'),
(15,'Ocna Șugatag, situată în inima Maramureșului, este o stațiune balneară cunoscută pentru apele sărate provenite din foste exploatări miniere. Aceste ape au efecte terapeutice remarcabile, fiind utilizate în tratarea afecțiunilor reumatologice, neurologice și ale sistemului locomotor. Stațiunea oferă piscine cu apă sărată, baze moderne de tratament și un cadru natural deosebit, ideal pentru relaxare și refacere. În plus, turiștii pot descoperi frumusețea tradițiilor maramureșene, bisericile de lemn și ospitalitatea localnicilor. Ocna Șugatag rămâne o destinație perfectă pentru cei care caută sănătate, liniște și autenticitate.','images/sugatag1.jpg, images/sugatag2.jpg, images/sugatag3.jpg, images/sugatag4.jpg'),
(16,'Praid, aflat în județul Harghita, este o destinație cunoscută pentru Salina Praid, una dintre cele mai mari saline din Europa, recunoscută pentru efectele sale benefice asupra afecțiunilor respiratorii. În subteran, salina oferă un microclimat unic, ideal pentru tratamente, dar și zone de agrement și spații de joacă. Pe lângă terapia salină, stațiunea pune la dispoziție băi sărate, trasee de drumeție prin peisajele montane și oportunități de explorare a tradițiilor secuiești. Datorită combinației dintre tratament, relaxare și natură, Praid atrage anual numeroși turiști în căutare de sănătate și odihnă.','images/praid1.jpg, images/praid2.jpg, images/praid3.jpg, images/praid4.jpg'),
(17,'Slănic Moldova, situată în pitorescul decor al Carpaților Orientali, este o stațiune balneară apreciată pentru izvoarele sale minerale cu efecte benefice asupra sănătății. Cunoscute pentru tratarea afecțiunilor digestive, renale și respiratorii, aceste ape atrag turiști în căutare de tratamente naturale. Stațiunea oferă aer curat, peisaje montane spectaculoase și multiple oportunități de recreere, inclusiv drumeții, sporturi de iarnă și plimbări relaxante prin Parcul Central. Datorită combinației dintre terapii balneare și activități în aer liber, Slănic Moldova este o destinație ideală pentru sănătate, relaxare și refacere.','images/slanic1.jpg, images/slanic2.jpg, images/slanic3.jpg, images/slanic4.jpg'),
(18,'Sovata, situată în inima Transilvaniei, este o stațiune balneară de renume, faimoasă pentru Lacul Ursu, un lac helioterm unic în Europa. Datorită combinației dintre apa sărată și stratificarea termică, lacul are efecte terapeutice remarcabile, fiind recomandat pentru tratarea afecțiunilor reumatologice, ginecologice și dermatologice. Pe lângă băile curative, stațiunea oferă trasee de drumeție prin păduri pitorești, centre de wellness și spa, precum și oportunități de relaxare pe tot parcursul anului. Atmosfera liniștită, aerul curat și frumusețea naturală transformă Sovata într-o destinație ideală pentru sănătate și recreere.','images/sovata1.jpg, images/sovata2.jpg, images/sovata3.jpg, images/sovata4.jpg');
