CREATE TABLE orase (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL, 
    descriere TEXT,
    imagine VARCHAR(255)
);


CREATE TABLE detalii_orase (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);



CREATE TABLE locuri_vizitat (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    nume VARCHAR(150) NOT NULL,
    descriere TEXT NOT NULL,
    imagine VARCHAR(255),
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);

CREATE TABLE orased (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL, 
    descriere TEXT,
    imagine VARCHAR(255)
);


CREATE TABLE detalii_orased (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES orased(id) ON DELETE CASCADE
);


CREATE TABLE statb (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nume VARCHAR(100) NOT NULL, 
    descriere TEXT,
    imagine VARCHAR(255)
);


CREATE TABLE detalii_statb (
    id INT AUTO_INCREMENT PRIMARY KEY,
    orase_id INT NOT NULL,
    descriere_lunga TEXT NOT NULL,
    galerie_foto TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES statb(id) ON DELETE CASCADE
);
























CREATE TABLE feedback (
  id int AUTO_INCREMENT PRIMARY KEY,
  name varchar(20) NOT NULL,
  email varchar(30) NOT NULL,
  feedbk varchar(1000) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE table auth(
  id int AUTO_INCREMENT PRIMARY KEY,
  username varchar(100) UNIQUE NOT NULL,
  parola varchar(100) NOT NULL
)

