    website VARCHAR(255),
    descriere_lunga TEXT NOT NULL,
    facilitati TEXT NOT NULL,
    meniu TEXT,
    imagine VARCHAR(255),
    progr TEXT NOT NULL,
    FOREIGN KEY (orase_id) REFERENCES orase(id) ON DELETE CASCADE
);




