INSERT INTO statm (nume, descriere, imagine) VALUES
('Arieșeni', 'Arieșeni, situată în Munții Apuseni, la granița dintre județele Alba și Bihor, este o destinație montană apreciată pentru pârtiile de schi din Vârtop și peisajele naturale spectaculoase, atrăgând turiști în toate anotimpurile.', 'images/arisieni.jpg'),
('Azuga', 'Azuga, situată pe Valea Prahovei, este o stațiune montană apreciată pentru pârtiile sale moderne de schi, precum Sorica și Cazacu. Oferă oportunități excelente pentru sporturi de iarnă și drumeții montane în sezonul cald​.', 'images/azuga.jpg'),
('Borșa', 'Borșa, situată în județul Maramureș, este o stațiune montană apreciată pentru peisajele sale pitorești și pentru pârtiile de schi moderne. Oferă oportunități excelente pentru sporturi de iarnă și drumeții montane în sezonul cald.', 'images/borsa.jpg'),
('Bușteni', 'Bușteni, situat pe Valea Prahovei, la poalele Munților Bucegi, este o stațiune montană renumită pentru peisajele sale spectaculoase și pentru atracții precum Crucea Eroilor și Castelul Cantacuzino.​', 'images/busteni.jpg'),
('Cavnic', 'Cavnic, situat în județul Maramureș, este o stațiune montană renumită pentru pârtiile sale de schi moderne și peisajele pitorești. Oferă oportunități excelente pentru sporturi de iarnă și drumeții montane în sezonul cald.', 'images/cavnic.jpg'),
('Cheia', 'Cheia, situată în județul Prahova, la poalele Masivului Ciucaș, este o stațiune montană apreciată pentru peisajele sale pitorești și aerul curat. Oferă oportunități excelente pentru drumeții și relaxare în natură', 'images/cheia.jpg'),
('Păltiniș', 'Păltiniș, situată în județul Sibiu, este cea mai veche și cea mai înaltă stațiune montană din România, fondată în 1894. Oferă pârtii de schi moderne, trasee montane pitorești și un climat alpin tonic, atrăgând turiști în toate anotimpurile.​', 'images/paltinis.jpg'),
('Poiana Brașov', 'Poiana Brașov, situată la poalele Masivului Postăvarul, este cea mai mare și modernă stațiune montană din România. Oferă pârtii de schi variate, peisaje spectaculoase și facilități turistice de top, atrăgând vizitatori în toate anotimpurile.​', 'images/poianabrasov.jpg'),
('Predeal', 'Predeal, situat pe Valea Prahovei, este cea mai înaltă stațiune urbană din România, aflată la altitudini între 1.030 și 1.110 metri. Înconjurată de Munții Bucegi, Postăvarul și Piatra Mare, oferă pârtii de schi moderne și trasee montane pitorești.', 'images/predeal.jpg'),
('Rânca', 'Rânca, situată pe Transalpina în Munții Parâng, este o stațiune tânără, ideală pentru schi, drumeții și off-road. Peisajele spectaculoase și aerul curat atrag turiști în orice anotimp, fiind o alegere liniștită și autentică.', 'images/ranca.jpg'),
('Stațiunea Semenic', 'Stațiunea Semenic, situată în Munții Banatului, la o altitudine de 1.400 de metri, oferă un sezon de schi extins și peisaje spectaculoase. Este o destinație ideală pentru iubitorii sporturilor de iarnă și drumețiilor montane.', 'images/semenic.jpg'),
('Sinaia', 'Sinaia, „Perla Carpaților”, este cunoscută pentru Castelul Peleș și peisajele spectaculoase. O destinație ideală pentru schi, drumeții și turism cultural, atrage turiști în toate anotimpurile datorită infrastructurii dezvoltate și accesibilității rapide din București.', 'images/sinaia.jpg'),
('Straja', 'Straja, situată în Munții Vâlcan, județul Hunedoara, la o altitudine de 1.445 metri, este o stațiune montană apreciată pentru pârtiile sale variate de schi și peisajele spectaculoase. Oferă oportunități excelente pentru sporturi de iarnă și activități montane în sezonul cald.', 'images/straja.jpg'),
('Vatra Dornei', 'Vatra Dornei, supranumită "Perla Bucovinei", este o stațiune montană și balneară situată la 802 metri altitudine, înconjurată de Munții Călimani, Suhard și Giumalău. Este renumită pentru izvoarele minerale, pârtiile de schi și peisajele pitorești. ', 'images/vatra dornei.jpg'),
('Voineasa', 'Voineasa, situată în județul Vâlcea, pe Valea Lotrului, la poalele Munților Lotru, este o stațiune montană apreciată pentru peisajele sale pitorești și aerul curat. Oferă oportunități excelente pentru odihnă, tratamente balneare și activități montane în toate anotimpurile.', 'images/voineasa.jpg');
INSERT INTO detalii_statm (orase_id, descriere_lunga, galerie_foto) VALUES
(1, 'Arieșeni este o stațiune montană situată în Munții Apuseni, la granița dintre județele Alba și Bihor, la o altitudine de aproximativ 950 de metri. Este renumită pentru peisajele sale pitorești și pentru numeroasele activități turistice disponibile pe tot parcursul anului. Iarna, stațiunea devine un punct de atracție pentru iubitorii sporturilor de iarnă, oferind pârtii de schi precum Vârtop I, echipată cu teleschi și nocturnă. Vara, zona este ideală pentru drumeții și explorarea obiectivelor naturale, cum ar fi Ghețarul Scărișoara, Peștera Urșilor și Cascada Vârciorog. Arieșeni oferă o gamă variată de opțiuni de cazare, de la pensiuni rustice la hoteluri moderne, și este o destinație potrivită atât pentru relaxare, cât și pentru aventură în mijlocul naturii.', 'images/arisieni1.jpg, images/arisieni2.jpg, images/arisieni3.jpg, images/arisieni4.jpg'),
(2, 'Azuga este o stațiune montană situată în județul Prahova, la poalele Munților Bucegi și Baiului, la o altitudine de aproximativ 920 de metri. Este renumită pentru pârtiile sale de schi, în special Pârtia Sorica, omologată de Federația Internațională de Schi, având o lungime de 2.100 de metri. Stațiunea dispune de infrastructură modernă, inclusiv telegondolă și instalații de zăpadă artificială, atrăgând atât schiori începători, cât și avansați. În sezonul cald, Azuga devine un punct de plecare pentru drumeții și activități în aer liber, oferind trasee montane variate și peisaje spectaculoase. De asemenea, stațiunea este cunoscută pentru aerul său curat și climatul tonic-stimulant, fiind recomandată pentru relaxare și refacere. Cu facilități de cazare diverse și accesibilitate ușoară, Azuga este o destinație potrivită pentru vacanțe active și recreative în orice anotimp.​', 'images/azuga1.jpg, images/azuga2.jpg, images/azuga3.jpg, images/azuga4.jpg'),
(3, 'Borșa este o stațiune montană situată în județul Maramureș, la poalele Munților Rodnei, la o altitudine de aproximativ 850 de metri. Este renumită pentru pârtiile sale de schi, în special Pârtia Runc-Știol, cu o lungime de 2.000 de metri și o diferență de nivel de 500 de metri. Stațiunea dispune de infrastructură modernă, inclusiv telescaun și instalații de zăpadă artificială, atrăgând atât schiori începători, cât și avansați. În sezonul cald, Borșa devine un punct de plecare pentru drumeții și activități în aer liber, oferind trasee montane variate și peisaje spectaculoase. De asemenea, stațiunea este cunoscută pentru aerul său curat și climatul tonic-stimulant, fiind recomandată pentru relaxare și refacere. Cu facilități de cazare diverse și accesibilitate ușoară, Borșa este o destinație potrivită pentru vacanțe active și recreative în orice anotimp.', 'images/borsa1.jpg, images/borsa2.jpg, images/borsa3.jpg, images/borsa4.jpg'),
(4, 'Bușteni este o stațiune montană situată în județul Prahova, la poalele Munților Bucegi, la o altitudine de aproximativ 880 de metri. Este cunoscută pentru peisajele sale impresionante și pentru atracții turistice deosebite. Printre acestea se numără Crucea Eroilor de pe Caraiman, un monument dedicat soldaților căzuți în Primul Război Mondial, și Castelul Cantacuzino, construit în stil neoromânesc în 1911. Stațiunea oferă oportunități excelente pentru drumeții montane, cu trasee ce duc către Babele, Sfinxul și Vârful Omu. De asemenea, Bușteni dispune de pârtii de schi moderne, precum Pârtia Kalinderu, echipată cu telescaun și instalații de zăpadă artificială. Accesul în stațiune este facil, fiind situată pe DN1 și având gară proprie. Bușteni este o destinație ideală pentru iubitorii de natură și aventură, oferind activități recreative pe tot parcursul anului.', 'images/busteni1.jpg, images/busteni2.jpg, images/busteni3.jpg, images/busteni4.jpg'),
(5, 'Cavnic este o stațiune montană situată în județul Maramureș, la poalele Munților Gutâi, la o altitudine cuprinsă între 550 și 1.120 de metri. Este renumită pentru domeniul schiabil SuperSki Cavnic, care include pârtii precum Roata și Icoana, cu o lungime totală de aproximativ 8,5 kilometri, dotate cu telescaune, nocturnă și tunuri de zăpadă. Stațiunea oferă condiții excelente pentru schiori de toate nivelurile, de la începători la avansați. În sezonul cald, Cavnic devine un punct de plecare pentru drumeții și activități în aer liber, oferind trasee montane variate și peisaje spectaculoase. De asemenea, stațiunea este cunoscută pentru aerul său curat și climatul tonic-stimulant, fiind recomandată pentru relaxare și refacere. Cu facilități de cazare diverse și accesibilitate ușoară, Cavnic este o destinație potrivită pentru vacanțe active și recreative în orice anotimp.​', 'images/cavnic1.jpg, images/cavnic2.jpg, images/cavnic3.jpg, images/cavnic4.jpg'),
(6, 'Cheia este o stațiune montană situată în județul Prahova, la poalele Masivului Ciucaș, la o altitudine de aproximativ 871 de metri. Este cunoscută pentru peisajele sale spectaculoase și pentru aerul curat, bogat în ozon, fiind recomandată pentru tratarea afecțiunilor respiratorii și asteniei nervoase. Stațiunea oferă numeroase trasee de drumeții către atracții naturale precum Vârful Ciucaș, Muntele Roșu și Culmea Zăganu. De asemenea, în apropiere se află Mănăstirea Cheia, un important lăcaș de cult ortodox, construit între 1835 și 1839, cu picturi murale realizate de Gheorghe Tattarescu. Cheia dispune de facilități de cazare variate, de la pensiuni rustice la hoteluri moderne, și este o destinație ideală pentru cei care doresc să se bucure de liniștea muntelui și de frumusețea naturii.', 'images/cheia1.jpg, images/cheia2.jpg, images/cheia3.jpg, images/cheia4.jpg'),
(7, 'Păltiniș este o stațiune montană situată în județul Sibiu, la poalele Munților Cindrel, la o altitudine de 1.442 de metri, fiind cea mai înaltă și una dintre cele mai vechi stațiuni din România, fondată în 1894 de Societatea Carpatină Ardeleană a Turiștilor. Stațiunea este renumită pentru climatul său alpin cu aer puternic ozonizat, benefic pentru sănătate. Iarna, Păltiniș oferă condiții excelente pentru sporturi de iarnă, cu pârtii de schi moderne și facilități adecvate. Vara, stațiunea devine un punct de plecare pentru drumeții și activități în aer liber, oferind trasee montane variate și peisaje spectaculoase. Printre atracțiile locale se numără Schitul Păltiniș și Casa memorială Constantin Noica. Stațiunea dispune de facilități de cazare diverse și este accesibilă atât cu mașina, cât și cu transportul public din Sibiu.', 'images/paltinis1.jpg, images/paltinis2.jpg, images/paltinis3.jpg, images/paltinis4.jpg'),
(8, 'Poiana Brașov este o stațiune montană de renume, situată în județul Brașov, la o altitudine de aproximativ 1.020 de metri, la poalele Masivului Postăvarul. Este cea mai mare și modernă stațiune de schi din România, dispunând de 12 pârtii cu grade diferite de dificultate, totalizând 24,5 km, și un sistem de transport pe cablu compus din telegondole, telescaune și teleschiuri. Stațiunea oferă facilități moderne de cazare, restaurante și centre de agrement, fiind potrivită atât pentru sporturile de iarnă, cât și pentru drumeții și activități recreative în sezonul cald. Poiana Brașov este ușor accesibilă din municipiul Brașov, aflat la doar 12 km distanță, și se află în apropierea unor atracții turistice importante, precum Castelul Bran și Cetatea Râșnov. Climatul său montan, aerul curat și peisajele deosebite fac din Poiana Brașov o destinație preferată pentru turiștii români și străini.', 'images/poianabrasov1.jpg, images/poianabrasov2.jpg, images/poianabrasov3.jpg, images/poianabrasov4.jpg'),
(9, 'Predeal este o stațiune montană de renume, situată în județul Brașov, pe Valea Prahovei, la o altitudine cuprinsă între 1.030 și 1.110 metri, fiind cel mai înalt oraș din România. Înconjurată de masivele Bucegi, Postăvarul și Piatra Mare, stațiunea oferă un cadru natural spectaculos, ideal pentru recreere și sporturi de iarnă. Domeniul schiabil include pârtii precum Clăbucet și Cocoșul, dotate cu telescaune, teleschiuri și instalații de zăpadă artificială. În sezonul cald, Predeal devine un punct de plecare pentru drumeții către atracții naturale precum Canionul Șapte Scări, Cascada Tamina și Peștera de Gheață. Stațiunea dispune de o gamă variată de unități de cazare și restaurante, fiind accesibilă atât rutier, cât și feroviar. Climatul montan, aerul curat și peisajele deosebite fac din Predeal o destinație preferată pentru turiștii români și străini.​', 'images/predeal1.jpg, images/predeal2.jpg, images/predeal3.jpg, images/predeal4.jpg'),
(10, 'Stațiunea Rânca se află la o altitudine de 1.600 m în Munții Parâng și este accesibilă prin celebra șosea Transalpina. Este o destinație montană în dezvoltare, apreciată pentru naturalețea peisajului și liniștea zonei. Iarna, dispune de mai multe pârtii de schi pentru începători și avansați, iar vara devine un punct de plecare pentru trasee montane spectaculoase spre Vârful Parângul Mare. Rânca este frecventată și de pasionații de sporturi cu motor, datorită curbelor spectaculoase ale Transalpinei. Oferă unități de cazare variate și o atmosferă autentică, departe de aglomerația stațiunilor comerciale. Este perfectă pentru cei care caută natură, aventură și odihnă activă.', 'images/ranca1.jpg, images/ranca2.jpg, images/ranca3.jpg, images/ranca4.jpg'),
(11, 'Stațiunea Semenic se află în județul Caraș-Severin, în Munții Banatului, la o altitudine de 1.400 de metri. Este renumită pentru sezonul de schi prelungit, zăpada persistând aproximativ șase luni pe an. Stațiunea dispune de pârtii variate, potrivite atât pentru începători, cât și pentru schiori avansați, precum Pârtia Casa Baraj – Văliug, cu o lungime de 800 de metri și instalații de nocturnă și zăpadă artificială . Pe lângă sporturile de iarnă, zona oferă oportunități pentru drumeții și explorarea Parcului Național Semenic-Cheile Carașului, care adăpostește păduri virgine de fag și peisaje carstice impresionante . Accesul se face din Reșița sau Caransebeș, iar stațiunea oferă facilități de cazare și închiriere de echipamente sportive.', 'images/semenic1.jpg, images/semenic2.jpg, images/semenic3.jpg, images/semenic4.jpg'),
(12, 'Stațiunea Sinaia este una dintre cele mai cunoscute destinații montane din România, situată pe Valea Prahovei, în inima Munților Bucegi. Sinaia îmbină natura cu istoria, oferind turiștilor acces la trasee spectaculoase de drumeții, dar și la atracții culturale importante, precum Castelul Peleș, Castelul Pelișor și Mănăstirea Sinaia. Iarna, stațiunea este un paradis pentru schiori, având pârtii variate, echipamente moderne și instalații pe cablu ce urcă până la Cota 2000. Sinaia este ușor accesibilă cu trenul sau mașina din București, fiind una dintre cele mai vizitate stațiuni montane din țară. Oferă cazare diversificată, de la pensiuni rustice la hoteluri de lux, și este gazda multor festivaluri și evenimente anuale.', 'images/sinaia1.jpg, images/sinaia2.jpg, images/sinaia3.jpg, images/sinaia4.jpg'),
(13, 'Straja este o stațiune montană situată în Munții Vâlcan, județul Hunedoara, la o altitudine de 1.445 metri. Stațiunea dispune de 12 pârtii de schi cu grade diferite de dificultate, însumând 26 km, dintre care 20 km beneficiază de zăpadă artificială. Cinci pârtii sunt echipate cu instalații de nocturnă, permițând schiatul până seara târziu. Accesul se face din orașul Lupeni, situat la 8 km distanță, fie pe un drum asfaltat, fie cu telegondola. Pe lângă sporturile de iarnă, Straja oferă atracții precum Schitul Straja, Crucea Eroilor și Tunelul celor 365 de Sfinți. Stațiunea este potrivită pentru familii și grupuri de prieteni, oferind facilități de cazare diverse și activități recreative pe tot parcursul anului.', 'images/straja1.jpg, images/straja2.jpg, images/straja3.jpg, images/straja4.jpg'),
(14, 'Vatra Dornei este o stațiune montană și balneară situată în județul Suceava, la confluența râurilor Dorna și Bistrița Aurie, la o altitudine de 802 metri. Înconjurată de Munții Călimani, Suhard și Giumalău, stațiunea oferă un climat subalpin cu aer curat și peisaje deosebite. Cunoscută pentru izvoarele sale minerale carbogazoase, termale, bicarbonatate, calcice, magnezice și sulfuroase, Vatra Dornei este o destinație ideală pentru tratamente balneare. În sezonul rece, atrage iubitorii sporturilor de iarnă, dispunând de pârtii de schi precum Dealu Negru și Parc, dotate cu instalații de transport pe cablu. De asemenea, stațiunea servește ca punct de plecare pentru drumeții montane și vizite la mănăstirile istorice din Bucovina. Accesibilă atât rutier, cât și feroviar, Vatra Dornei oferă o gamă variată de opțiuni de cazare și facilități turistice.', 'images/vatra dornei1.jpg, images/vatra dornei2.jpg, images/vatra dornei3.jpg, images/vatra dornei4.jpg'),
(15, 'Voineasa este o stațiune montană situată în județul Vâlcea, pe Valea Lotrului, la o altitudine de 600–800 de metri. Înconjurată de păduri de brad, molid și fag, stațiunea oferă un climat tonic-excitant, cu aer curat, bogat în aerosoli ionizați și ozonizați, benefic pentru sănătate. Este recomandată pentru tratarea afecțiunilor respiratorii, nevrozelor astenice, surmenajului fizic și intelectual, anemiilor și bolilor aparatului locomotor, dispunând de o bază de tratament modernă. În sezonul rece, Voineasa atrage iubitorii sporturilor de iarnă, fiind aproape de domeniul schiabil Transalpina Ski Resort, situat la Obârșia Lotrului, la 34 km distanță. În sezonul cald, stațiunea devine un punct de plecare pentru drumeții către atracții naturale precum Lacul Vidra, Transalpina și Cheile Latoriței. Voineasa oferă facilități de cazare variate și este accesibilă rutier, fiind situată la aproximativ 80 km de Râmnicu Vâlcea.', 'images/voineasa1.jpg, images/voineasa2.jpg, images/voineasa3.jpg, images/voineasa4.jpg');
INSERT INTO locuri_vizitat_statm (statm_id, nume, descriere, imagine) VALUES
(1, 'Peștera Scărișoara','Peștera Scărișoara adăpostește unul dintre cei mai mari ghețari subterani din România și Europa. Se află în Munții Apuseni, la aproximativ 20 km de Arieșeni. Intrarea se face printr-un aven adânc de peste 40 de metri, iar în interior se pot admira formațiuni spectaculoase de gheață. Ghețarul are o vechime de mii de ani și atrage numeroși turiști anual. Este un loc ideal pentru cei pasionați de speologie sau aventură. În sezonul cald, interiorul păstrează o temperatură scăzută constantă.', 'arie1.jpg'),
(1, 'Vârful Bihorul','Cel mai înalt vârf din Munții Apuseni, Vârful Bihorul atinge altitudinea de 1849 metri. Este un punct de atracție pentru drumeți și montaniarzi, oferind priveliști spectaculoase asupra zonei înconjurătoare. Traseul către vârf este mediu ca dificultate, traversând păduri, poieni și stâncării. Zona este cunoscută pentru biodiversitate, fiind parte din Parcul Natural Apuseni. Odată ajuns în vârf, turiștii sunt răsplătiți cu o panoramă de neuitat asupra Munților Apuseni și a peisajului carstic.', 'arie2.jpg'),
(1, 'Pârtia de schi Arieșeni','Situată în apropierea stațiunii, pârtia de schi Arieșeni este ideală pentru sporturile de iarnă, în special pentru începători și familii. Pârtia este dotată cu teleschi, echipamente de închiriat și monitori autorizați. Sezonul de schi durează, de obicei, din decembrie până în martie. Pe lângă schi, zona permite și activități precum săniuș sau snowboard. Este un punct de atracție pentru turiști din toată țara, fiind una dintre cele mai accesibile pârtii din Munții Apuseni.', 'arie3.jpg'),
(1, 'Satul Gârda de Sus','Gârda de Sus este un sat pitoresc din apropierea Arieșeniului, renumit pentru tradițiile păstrate și peisajul natural impresionant. Casele vechi din lemn, ospitalitatea localnicilor și produsele tradiționale atrag turiști interesați de cultura locală. Satul este punct de plecare pentru vizitarea peșterilor, cascadei Vârciorog sau Ghețarului de la Scărișoara. Este un loc ideal pentru ecoturism și pentru cei care caută liniștea rurală. Aici se pot experimenta bucate tradiționale din Țara Moților.', 'arie4.jpg'),
(1, 'Peștera Ghețarul de la Vârtop','Peștera Ghețarul de la Vârtop este cunoscută pentru descoperirea unei amprente fosilizate de om preistoric și pentru formațiunile spectaculoase de gheață și stalactite. Situată în apropierea satului Casa de Piatră, peștera este protejată și poate fi vizitată doar cu ghid. Este un loc fascinant pentru pasionații de speologie și istorie naturală. Traseul până la peșteră traversează păduri și pășuni montane. Vizita oferă o experiență inedită în adâncurile Apusenilor.', 'arie5.jpg'),
(2, 'Pârtia Sorica','Cea mai lungă pârtie de schi din România, Pârtia Sorica din Azuga se întinde pe aproximativ 2 km și este omologată pentru competiții internaționale. Este potrivită atât pentru începători cât și pentru avansați, oferind o panoramă superbă spre Munții Bucegi. Zona este dotată cu telescaun, nocturnă și tunuri de zăpadă. Este una dintre cele mai populare destinații de schi de pe Valea Prahovei. Pe lângă schi, în zonă se pot închiria și sănii sau echipamente pentru snowboard.', 'azu1.jpg'),
(2, 'Traseul spre Cabana Urechea','Traseul spre Cabana Urechea este un drum de munte liniștit, potrivit pentru drumeții de o zi. Începe din Azuga și urmează un drum forestier prin păduri dese. La cabană, turiștii pot face popas și admira priveliștile din Munții Baiului. Este o alegere ideală pentru cei care doresc o evadare din agitația stațiunii, fiind mai puțin frecventat. Traseul este accesibil tot timpul anului și nu necesită echipament montan special.', 'azu2.jpg'),
(2, 'Râul Azuga','Râul Azuga străbate întreaga stațiune, oferind un cadru pitoresc și răcoritor pe timp de vară. Malurile râului sunt ideale pentru plimbări, pescuit sportiv sau picnicuri. Peisajul este completat de poduri rustice și vegetație de munte. Râul contribuie la farmecul natural al stațiunii și este adesea fotografiat de turiști. Iarna, formează steluțe și gheață pe maluri, adăugând o notă de basm peisajului urban.', 'azu3.jpg'),
(2, 'Telecabina Azuga', 'Telecabina Azuga oferă o modalitate excelentă de a ajunge în vârful munților pentru a te bucura de peisaje montane spectaculoase. Cu o înălțime de 1.400 m, telecabina asigură o priveliște impresionantă asupra stațiunii și a împrejurimilor. Este o opțiune ideală pentru turiștii care doresc să exploreze zona la altitudine sau să practice sporturi de iarnă, fiind un punct de plecare pentru diverse trasee montane.', 'azu4.jpg'),
(2, 'Biserica din Azuga', 'Biserica Ortodoxă „Sfântul Nicolae” din Azuga este un loc de cult important pentru comunitatea locală și pentru turiștii care vizitează stațiunea. Construită în stilul arhitectural tradițional românesc, biserica impresionează prin detaliile sale și prin atmosfera liniștită. De asemenea, este un loc de refugiu spiritual pentru cei care doresc să se conecteze cu tradițiile religioase din zonă.', 'azu5.jpg'),
(3, 'Cascadele Cailor', 'Cascadele Cailor sunt una dintre cele mai spectaculoase atracții naturale din zona Borsa. Situate la o altitudine de 1.300 m, aceste cascade impresionante au o înălțime totală de aproximativ 90 de metri și sunt cele mai înalte din România. Căderea apei formează o priveliște uimitoare, fiind înconjurată de o vegetație deosebită. Accesul se face pe un traseu montan, iar vizita la cascade este un must-do pentru iubitorii de natură și drumeții.', 'bor1.jpg'),
(3, 'Vârful Ignis', 'Vârful Ignis, aflat la o altitudine de 1.800 m, este o destinație populară pentru drumeții și iubitorii de alpinism din zona Borsa. De pe acest vârf, vizitatorii pot admira o panoramă deosebită asupra Munților Maramureșului și a întregii regiuni. Traseele de acces sunt variate, iar peisajul montan din jur este un adevărat spectacol natural, perfect pentru fotografii și momente de relaxare în mijlocul naturii.', 'bor2.jpg'),
(3, 'Muntele Şurul', 'Muntele Şurul se află în apropierea stațiunii Borsa și reprezintă o destinație ideală pentru iubitorii de drumeție montană. Traseele care duc până pe vârf sunt de dificultate medie și sunt foarte apreciate de cei care doresc să exploreze peisaje montane spectaculoase. Odată ajunși pe vârf, vizitatorii pot observa o panoramă deosebită asupra întregii zone și a Munților Rodnei. Zona este perfectă pentru o escapadă în natură, departe de agitația cotidiană.', 'bor3.jpg'),
(3, 'Parcul Național Munții Maramureșului', 'Parcul Național Munții Maramureșului este un loc deosebit din apropiere de Borsa, cunoscut pentru peisajele sale montane spectaculoase și biodiversitatea extraordinară. Aici, turiștii pot explora păduri virgine, pot observa specii rare de animale și pot practica drumeții pe trasee variate. Parcul este un refugiu pentru cei care vor să petreacă timp în mijlocul naturii, având multiple oportunități de relaxare și aventură. Este o destinație populară pentru iubitorii de ecoturism.', 'bor4.jpg'),
(3, 'Călătoria cu Mocănița', 'Călătoria cu Mocănița este una dintre cele mai autentice experiențe turistice din zona Borsa. Trenul cu aburi, care circulă pe vechiul traseu feroviar din Maramureș, oferă o excursie pitorescă prin pădurile și munții din jurul stațiunii. Turiștii pot experimenta o atmosferă deosebită, admirând peisaje montane unice și traversând sate tradiționale. Mocănița este o atracție emblematică a regiunii și o modalitate perfectă de a descoperi frumusețea naturală a Maramureșului.', 'bor5.jpg'),
(4, 'Cascada Urlătoarea', 'Cascada Urlătoarea este una dintre cele mai spectaculoase cascade din Munții Bucegi și un simbol al stațiunii Bușteni. Situată la aproximativ o oră de mers pe jos din stațiune, traseul este accesibil și pitoresc. Zgomotul puternic al apei oferă o experiență auditivă deosebită.', 'bus1.jpg'),
(4, 'Crucea Eroilor de pe Caraiman', 'Monumentul Eroilor de pe Vârful Caraiman, construit între 1926-1928, este un simbol național dedicat soldaților căzuți în Primul Război Mondial. Situat la peste 2.200 m altitudine, oferă o panoramă impresionantă asupra Văii Prahovei.', 'bus2.jpg'),
(4, 'Castelul Cantacuzino', 'Castelul Cantacuzino, construit în 1911, impresionează prin arhitectura neoromânească și grădinile sale elegante. Situat în centrul stațiunii Bușteni, oferă expoziții de artă, evenimente culturale și o priveliște superbă asupra Bucegilor.', 'bus3.jpg'),
(4, 'Sfinxul din Bucegi', 'Sfinxul este o formațiune naturală din platoul Bucegi, aflată la 2.216 m altitudine. Se aseamănă cu un chip uman și este învăluit în mistere și legende. Accesibil din Bușteni cu telecabina sau prin trasee montane.', 'bus4.jpg'),
(4, 'Babele', 'Babele sunt formațiuni stâncoase aflate în apropierea Sfinxului, numite astfel datorită asemănării cu niște siluete de bătrâne. Reprezintă un punct turistic popular, ușor accesibil din Bușteni și adesea vizitat împreună cu Sfinxul.', 'bus5.jpg'),
(5, 'Pârtia Icoana', 'Pârtia Icoana este una dintre cele mai populare atracții din Cavnic pentru iubitorii sporturilor de iarnă. Oferă condiții excelente pentru schi și snowboarding, fiind dotată cu instalații moderne și telescaun.', 'cav1.jpg'),
(5, 'Pârtia Roata', 'Situată în apropiere de Cavnic, Pârtia Roata atrage numeroși turiști iarna datorită lungimii și nivelului variat de dificultate. Zona este bine întreținută și oferă facilități pentru toate categoriile de schiori.', 'cav2.jpg'),
(5, 'Drumul Regelui', 'Drumul Regelui este un traseu spectaculos ce leagă Cavnic de zona Gutâi. Perfect pentru drumeții sau ciclism montan, oferă peisaje superbe și trece prin păduri dese și poieni liniștite.', 'cav3.jpg'),
(5, 'Rezervația Creasta Cocoșului', 'Creasta Cocoșului este o formațiune stâncoasă spectaculoasă, situată în Munții Gutâi. Este o destinație ideală pentru alpiniști și pasionații de fotografie, datorită panoramelor deosebite.', 'cav4.jpg'),
(5, 'Muzeul de Mineralogie Cavnic', 'Cavnic are o bogată tradiție minieră, iar muzeul local expune o colecție impresionantă de minerale rare, fosile și obiecte folosite în exploatarea minieră. O incursiune interesantă în istoria zonei.', 'cav5.jpg'),
(5, 'Lacul Albastru Baia Sprie (aproape de Cavnic)', 'La doar câțiva kilometri de Cavnic se află Lacul Albastru, un fenomen unic datorită nuanței intense a apei. Este înconjurat de pădure și oferă o atmosferă de relaxare și mister.', 'cav6.jpg'),
(5, 'Traseul spre Vârful Gutâi', 'Vârful Gutâi, cu o altitudine de peste 1.400 m, este un punct de atracție pentru drumeți. Traseul pornește din apropiere de Cavnic și oferă o experiență montană autentică, cu peisaje alpine spectaculoase.', 'cav7.jpg'),
(6, 'Mănăstirea Cheia', 'Mănăstirea Cheia, situată în inima Munților Ciucaș, este un important loc de reculegere și atracție turistică. Construită în stil brâncovenesc, aceasta impresionează prin arhitectura sa și liniștea din jur. Vizitatorii pot admira picturile murale din biserică și se pot bucura de peisajele montane din jur. Este un loc perfect pentru cei care caută un refugiu spiritual, dar și pentru pasionații de istorie și cultură. Zona este înconjurată de păduri dese, iar aerul curat și liniștea contribuie la o experiență profund relaxantă și memorabilă.', 'che1.jpg'),
(6, 'Vârful Ciucaș', 'Vârful Ciucaș, aflat la 1.954 m altitudine, este unul dintre cele mai cunoscute vârfuri din Carpații de Curbură. Traseul spre vârf pornește din Cheia și oferă priveliști spectaculoase asupra munților înconjurători. Drumul este presărat cu formațiuni stâncoase spectaculoase, iar flora alpină este bogată și variată. Este o destinație ideală pentru iubitorii de natură, drumeții și fotografie montană. În sezonul cald, traseul este accesibil pentru majoritatea turiștilor, oferind o experiență de neuitat celor care ajung până în vârf.', 'che2.jpg'),
(6, 'Cabana Vârful Ciucaș', 'Cabana Vârful Ciucaș este situată la poalele masivului cu același nume, fiind un punct de plecare pentru numeroase trasee montane. Cabana oferă condiții bune de cazare și un cadru natural deosebit, înconjurat de păduri și stânci impunătoare. Este locul perfect pentru cei care vor să petreacă mai multe zile în natură și să exploreze Munții Ciucaș. De aici, turiștii pot porni spre Vârful Ciucaș, Tigăile Mari sau Bratocea. În serile liniștite, se pot admira apusurile spectaculoase care colorează munții în nuanțe calde.', 'che.jpg'),
(6, 'Tigăile Mari', 'Tigăile Mari reprezintă un grup impresionant de formațiuni stâncoase, aflate în Masivul Ciucaș, în apropierea stațiunii Cheia. Accesul se face prin trasee montane marcate, de dificultate medie. Stâncile au forme spectaculoase, sculptate de vânt și ploaie, fiind asemănate cu tigăi sau alte obiecte casnice. Zona este foarte populară printre drumeți și fotografi de natură, datorită peisajelor dramatice. Este și un loc liniștit, perfect pentru meditație sau picnicuri. Din vârful stâncilor se pot admira panorame impresionante asupra întregii regiuni montane.', 'che.jpg'),
(6, 'Poiana Stânii', 'Poiana Stânii este o zonă pitorească situată aproape de Cheia, ideală pentru relaxare, plimbări ușoare și picnicuri. Accesibilă pe jos sau cu bicicleta, poiana oferă o oază de liniște în mijlocul naturii. Vara, este plină de flori sălbatice, iar toamna, pădurile din jur capătă culori spectaculoase. Este un loc frecventat de familii și iubitori de natură care doresc să evadeze din agitația orașului. De aici, se pot începe și trasee către vârfurile din Munții Ciucaș. Aerul curat și priveliștea spre munți o fac o destinație preferată în orice anotimp.', 'che5.jpg'),
(6, 'Cheile Cheiței', 'Cheile Cheiței sunt o formațiune naturală spectaculoasă situată în apropiere de Cheia, într-o zonă mai puțin cunoscută, dar extrem de pitorească. Accesul se face pe un traseu marcat, care șerpuiește prin pădure și ajunge în inima cheilor. Pereții stâncoși înalți, acoperiți cu mușchi și vegetație, creează un peisaj de poveste. Este locul ideal pentru drumeții ușoare și explorări în natură. Sunetul apei care curge prin defileu adaugă un farmec aparte. Turiștii pot face pauze de relaxare și pot admira fauna și flora locală, extrem de variate.', 'che6.jpg'),
(7, 'Pârtia Arena Platoș', 'Arena Platoș este una dintre cele mai moderne stațiuni de schi din zona Păltiniș, oferind condiții excelente pentru sporturile de iarnă. Are mai multe pârtii amenajate pentru toate nivelurile, un snowpark pentru freestyle, instalație de nocturnă și telescaun rapid. Pe lângă schi și snowboard, aici se organizează evenimente tematice, concursuri și activități recreative pentru copii. Există școli de schi, zone de tubing și facilități de închiriere echipamente. Arena Platoș combină distracția cu peisajele montane spectaculoase, fiind o destinație perfectă pentru o vacanță activă la munte.', 'pa1.jpg'),
(7, 'Rezervația Iezerele Cindrelului', 'Această rezervație naturală spectaculoasă se află în Munții Cindrel și include două lacuri glaciare formate în timpul erei glaciare. Iezerele Cindrelului oferă un peisaj de vis, cu ape limpezi și albastre, înconjurate de pășuni alpine și stânci impresionante. Accesul se face prin trasee montane marcate, fiind o alegere ideală pentru drumeții și iubitorii de natură. Zona este protejată și reprezintă un important habitat pentru faună montană rară. Este un loc liniștit, departe de aglomerație, perfect pentru explorare și relaxare în mijlocul naturii.', 'pa2.jpg'),
(7, 'Biserica din Păltiniș', 'Biserica din Păltiniș este o mică bijuterie arhitecturală din lemn, construită în stil tradițional românesc. Situată în centrul stațiunii, este înconjurată de brazi seculari și oferă o atmosferă calmă, perfectă pentru reculegere. Clădirea impresionează prin simplitatea sa rustică și prin legătura profundă cu natura și spiritualitatea locului. Este un punct de atracție pentru turiștii interesați de patrimoniu cultural și religios. Deși modestă ca dimensiuni, biserica are un farmec aparte și oferă o experiență liniștitoare, mai ales în serile de iarnă când e luminată discret.', 'pa3.jpg'),
(7, 'Traseul Păltiniș – Vârful Cindrel', 'Traseul spre Vârful Cindrel pornește din Păltiniș și urcă până la 2244 metri, fiind unul dintre cele mai populare itinerarii montane din zonă. Potrivit pentru drumeții cu experiență medie, traseul străbate păduri de conifere, goluri alpine și pajiști spectaculoase, oferind priveliști impresionante asupra Munților Cindrel și împrejurimilor. Pe parcurs se pot observa lacuri glaciare și formațiuni stâncoase unice. Vârful Cindrel oferă o panoramă superbă, iar în zilele senine se pot vedea chiar Munții Făgăraș. Este o experiență montană completă și memorabilă.', 'pa4.jpg'),
(8, 'Pârtia Bradul', 'Pârtia Bradul este ideală pentru începători și familii cu copii, fiind ușor accesibilă și bine întreținută. Situată în inima stațiunii Poiana Brașov, beneficiază de instalații moderne de teleski și nocturnă, ceea ce permite schiul până seara târziu. Este locul perfect pentru lecții de inițiere și plimbări relaxante pe zăpadă. În apropiere se găsesc cafenele, centre de închiriere echipament și școli de schi. Atmosfera este prietenoasă, iar împrejurimile montane creează un cadru de poveste pentru o zi activă în aer liber.', 'po1.jpg'),
(8, 'Lacul Miorița', 'Lacul Miorița este un mic lac artificial situat chiar în centrul stațiunii Poiana Brașov. Încadrat de păduri de conifere și alei pitorești, este un loc de relaxare ideal vara, perfect pentru plimbări și fotografii de vacanță. Iarna, peisajul devine feeric, cu lacul înghețat și zăpada care acoperă totul în jur. În apropiere se află terase și restaurante, ceea ce îl face o destinație excelentă pentru o pauză liniștită între activitățile montane. Este adesea punct de plecare pentru drumeții ușoare în stațiune.', 'po2.jpg'),
(8, 'Telecabina Kanzel', 'Telecabina Kanzel este una dintre cele mai spectaculoase modalități de a ajunge rapid în vârful Masivului Postăvarul. Traseul oferă priveliști uimitoare asupra pădurilor și văilor din jur, iar în zilele senine se pot observa Munții Bucegi și chiar orașul Brașov. Este o experiență unică atât pentru schiori, cât și pentru turiști care doresc să urce la altitudine pentru fotografii sau pentru a porni în trasee montane. Stația superioară oferă acces la pârtii dificile și cabane montane tradiționale.', 'po3.jpg'),
(8, 'Vârful Postăvarul', 'Vârful Postăvarul (1799 m) este cel mai înalt punct din apropierea Poienii Brașov și o destinație preferată de drumeți și iubitori ai sporturilor de iarnă. Traseul spre vârf poate fi parcurs fie pe jos, fie cu telecabina, iar panorama oferită de sus este impresionantă: se văd Munții Piatra Craiului, Bucegii și întreaga depresiune a Brașovului. Iarna, zona este acoperită de zăpadă și oferă acces rapid la pârtii spectaculoase. Vara, este un loc excelent pentru picnicuri și explorare.', 'po4.jpg'),
(8, 'Cabana Postăvaru', 'Situată la altitudine, Cabana Postăvaru este un punct emblematic pentru turiștii care vizitează Poiana Brașov. Construită în stil tradițional, cabana oferă mâncare autentic românească, băuturi calde și o atmosferă montană rustică și primitoare. Este un loc perfect pentru odihnă după o zi de schi sau drumeție. Din cabană se poate admira o priveliște spectaculoasă asupra vârfului Postăvarul și a pădurilor înconjurătoare. Este accesibilă atât pe jos, cât și cu telecabina sau schiurile, fiind una dintre cele mai iubite destinații locale.', 'po5.jpg'),
(9, 'Pârtia Clăbucet', 'Pârtia Clăbucet este una dintre cele mai populare destinații de schi din Predeal, perfectă pentru toate nivelurile de experiență. Cu instalații moderne de telescaun și teleschi, tunuri de zăpadă și iluminare nocturnă, oferă condiții excelente pentru sporturi de iarnă. Pârtia are mai multe variante – pentru începători, intermediari și avansați. Atmosfera este animată, iar zona este bine dotată cu centre de închiriere, cafenele și școli de schi. Iarna, Clăbucetul este inima Predealului și atrage turiști din toată țara.', 'pred1.jpg'),
(9, 'Cabana Susai', 'Situată la aproximativ 8 km de Predeal, Cabana Susai este o destinație de neratat pentru iubitorii de natură și drumeții. Accesibilă pe jos, cu bicicleta sau chiar cu snowmobilul iarna, cabana oferă o atmosferă autentic montană. Din curtea cabanei se deschid priveliști spectaculoase spre Munții Baiului și Bucegi. Este un loc ideal pentru o pauză cu mâncare tradițională românească, ceai fierbinte și aer curat de munte. Traseul este potrivit și pentru familii cu copii, fiind o experiență accesibilă și relaxantă.', 'pred2.jpg'),
(9, 'Vârful Clăbucetul Taurului', 'Pentru cei pasionați de drumeții, vârful Clăbucetul Taurului (1520 m) oferă un traseu ușor spre mediu, cu panorame superbe asupra Văii Prahovei și Munților Postăvaru. Traseul pornește din Predeal și traversează păduri de conifere și poieni alpine. În vârf, turiștii sunt recompensați cu o vedere largă și liniște deplină. Este locul ideal pentru picnic, fotografie de peisaj sau pur și simplu pentru contemplare. Traseul este bine marcat și recomandat în toate anotimpurile, cu echipament adecvat.', 'pred3.jpg'),
(9, 'Mănăstirea Sfântul Nicolae', 'Mănăstirea Sfântul Nicolae din Predeal este o oază de liniște și spiritualitate în mijlocul naturii. Aflată la marginea pădurii, mănăstirea impresionează prin arhitectura sa tradițională, pictura interioară detaliată și atmosfera de reculegere. Turiștii vin aici nu doar pentru latura religioasă, ci și pentru frumusețea naturală care înconjoară locul. În apropiere se află un izvor de munte, iar drumul către mănăstire este o plimbare liniștită, potrivită oricărui vizitator. Este o destinație ideală pentru o pauză de calm și reflecție.', 'pred4.jpg'),
(9, 'Trei Brazi', 'Trei Brazi este un punct panoramic renumit din Predeal, situat la câțiva kilometri de oraș. Aici se află o cabană tradițională, o zonă largă de belvedere și mai multe trasee montane marcate. Locul este ideal pentru relaxare în aer liber, cu peisaje spectaculoase spre Bucegi, Piatra Mare și Postăvaru. Accesibil cu mașina sau pe jos, Trei Brazi este preferat de familii, cupluri și fotografi de natură. Iarna, zona devine un paradis pentru săniuș și drumeții în zăpadă. Este simbolul Predealului și un loc perfect pentru escapade scurte.', 'pred5.jpg'),
(10, 'Pârtia Rânca', 'Pârtia Rânca este una dintre cele mai populare atracții de iarnă din Munții Parâng. Cu mai multe trasee de dificultăți diferite, stațiunea oferă condiții excelente pentru schiori și snowboarderi, atât începători, cât și avansați. Instalațiile moderne, teleskiul și atmosfera prietenoasă transformă pârtia într-un loc ideal pentru vacanțele de iarnă. Zona este bine deservită de centre de închiriere echipamente, școli de schi și restaurante. Iarna, peisajul devine de poveste, iar pârtiile sunt mereu bine întreținute. Este locul perfect pentru distracție, adrenalină și relaxare în natură.', 'ranc1.jpg'),
(10, 'Vârful Păpușa', 'Vârful Păpușa (2135 m) este cel mai cunoscut vârf din apropierea stațiunii Rânca, oferind o priveliște panoramică impresionantă asupra Munților Parâng. Traseul spre vârf este accesibil din șoseaua Transalpina și poate fi parcurs de majoritatea drumeților într-un ritm lejer. Pe drum, turiștii sunt răsplătiți cu peisaje alpine spectaculoase, pășuni întinse și aer curat de munte. În zilele senine se poate vedea o mare parte din lanțul carpatic. Este o destinație emblematică pentru iubitorii de munte și o experiență ce merită trăită la pas.', 'ranc2.jpg'),
(10, 'Transalpina – Drumul Regelui', 'Transalpina, cunoscută și ca „Drumul Regelui”, traversează Munții Parâng și atinge altitudini de peste 2.100 m, fiind cel mai înalt drum rutier din România. Segmentul care trece prin Rânca este considerat unul dintre cele mai spectaculoase trasee auto și moto din țară. Peisajele sunt uimitoare, cu serpentine dramatice, păduri alpine și vârfuri stâncoase. Vara, drumul atrage turiști din toată Europa, iar iarna, porțiunea dinspre Rânca devine o provocare pentru amatorii de off-road și sporturi de iarnă. Este un simbol al aventurii montane.', 'ranc3.jpg'),
(10, 'Stațiunea Rânca – Belvedere', 'Punctul de belvedere din stațiunea Rânca oferă unele dintre cele mai impresionante priveliști montane din sudul țării. Aflat la marginea stațiunii, acest loc este ideal pentru răgaz, fotografii și contemplarea naturii. De aici se pot admira Vârful Păpușa, Transalpina șerpuind printre culmi și pădurile dese ale Parângului. Locul este amenajat cu bănci și foișoare, fiind perfect pentru o pauză în natură. Este o oprire obligatorie pentru oricine vizitează Rânca, indiferent de sezon.', 'ranc4.jpg'),
(11, 'Pârtia Semenic – Gozna', 'Pârtia Semenic – Gozna este principala atracție de iarnă a stațiunii Semenic, ideală pentru iubitorii de schi și snowboard. Dispune de telescaun și instalații moderne, fiind potrivită pentru toate nivelurile de experiență. Iarna, zăpada abundentă asigură condiții excelente, iar atmosfera liniștită o face o alternativă perfectă la stațiunile aglomerate. Pârtia oferă și o panoramă minunată asupra Munților Banatului. În apropiere se află centre de închiriere echipamente, cabane și restaurante tradiționale, creând un cadru complet pentru o vacanță montană autentică.', 'seme1.jpg'),
(11, 'Vârful Semenic', 'Vârful Semenic, cu o altitudine de 1446 metri, este cel mai înalt punct din zonă și un reper geografic al Banatului Montan. Este accesibil atât iarna cât și vara, iar traseul până la vârf oferă priveliști spectaculoase asupra platoului Semenic și a pădurilor dese care îl înconjoară. Vara, este un loc excelent pentru drumeții ușoare și picnicuri, iar iarna, devine o zonă ideală pentru schi fond și plimbări prin zăpadă. Aerul curat și liniștea muntelui atrag turiști în căutare de relaxare în mijlocul naturii.', 'seme2.jpg'),
(11, 'Lacul Gozna (Văliug)', 'Situat la poalele Munților Semenic, Lacul Gozna este o destinație populară pentru relaxare și sporturi nautice în sezonul cald. Aici turiștii pot înota, închiria bărci sau pur și simplu se pot bucura de peisajul natural din jur. Lacul este înconjurat de păduri și pensiuni rustice, fiind perfect pentru un weekend în natură. Vara, zona devine vibrantă, cu terase pe mal, muzică live și evenimente. Iarna, priveliștea lacului înghețat este la fel de fermecătoare. Este o destinație completă, potrivită pentru toate anotimpurile.', 'seme3.jpg'),
(11, 'Rezervația Naturală Cheile Carașului', 'Cheile Carașului, aflate în apropierea Munților Semenic, sunt un adevărat spectacol al naturii: stânci abrupte, peșteri ascunse, păduri dese și trasee de drumeție spectaculoase. Rezervația este protejată și oferă habitat pentru numeroase specii rare de floră și faună. Este o destinație preferată de fotografi, speologi și iubitori de natură sălbatică. Traseele sunt marcate și accesibile, iar peisajul este impresionant în orice anotimp. O zi petrecută aici oferă liniște, aventură și o conexiune profundă cu natura sălbatică a Banatului.', 'seme4.jpg'),
(11, 'Stațiunea Semenic – Platou', 'Platoul Semenic este inima stațiunii, situat la peste 1400 metri altitudine, cu o atmosferă autentică de munte și aer curat de pădure. Aici se află cabane, pensiuni, pârtii și puncte de belvedere. Iarna, platoul se transformă într-un paradis alb pentru sporturile de zăpadă, iar vara devine punctul de pornire pentru drumeții și ture montane. Zona este recunoscută pentru liniștea și lipsa aglomerației, atrăgând turiști care caută un colț de natură neatins. Este un loc unde timpul pare că stă pe loc și natura domină peisajul.', 'seme5.jpg'),
(12, 'Castelul Peleș', 'Castelul Peleș din Sinaia este una dintre cele mai frumoase reședințe regale din Europa. Construit între 1873 și 1914, castelul este un exemplu remarcabil de arhitectură neo-renascentistă, cu interioare luxuriante și o vastă colecție de artă. Pe lângă frumusețea clădirii, castelul este înconjurat de un parc larg, unde turiștii se pot plimba și se pot bucura de priveliștile montane. Este, fără îndoială, una dintre cele mai importante atracții din România, atrăgând anual mii de vizitatori din întreaga lume.', 'sina1.jpg'),
(12, 'Pârtia de Schi Sinaia', 'Pârtia de Schi Sinaia este ideală pentru iubitorii sporturilor de iarnă, oferind pârtii de diferite nivele de dificultate, de la începători până la avansați. Stațiunea Sinaia este dotată cu instalații moderne de telescaun și teleschi, iar pârtiile sunt bine întreținute pe tot parcursul sezonului. Pe lângă schi, turiștii se pot bucura și de alte activități de iarnă, precum snowboard și săniuș. Sinaia oferă o atmosferă de vacanță perfectă, fiind o destinație populară în România pentru sporturile de iarnă.', 'sina2.jpg'),
(12, 'Mănăstirea Sinaia', 'Mănăstirea Sinaia, construită în 1695, este un loc de cult important al regiunii și unul dintre cele mai vechi și mai frumoase lăcașuri de cult din România. Mănăstirea este cunoscută pentru arhitectura sa tradițională și picturile religioase deosebite, iar în curtea interioară se află o atmosferă de liniște și meditație. Este un loc perfect pentru cei care doresc să se conecteze cu istoria religioasă a regiunii și să se bucure de pacea naturală oferită de Munții Bucegi.', 'sina3.jpg'),
(12, 'Parcul Dimitrie Ghica', 'Parcul Dimitrie Ghica este un loc minunat de relaxare și recreere în Sinaia, situat aproape de centrul orașului. Parcul oferă o atmosferă calmă, cu alei pietonale, fântâni și un lac mic, fiind ideal pentru o plimbare relaxantă. Acesta este un loc frecventat de turiști și localnici deopotrivă, iar în timpul verii, parcul găzduiește frecvent evenimente culturale și muzicale. De asemenea, în parc se află și o statuie a fondatorului Sinaiei, care adaugă un plus de valoare istorică acestui loc.', 'sina4.jpg'),
(12, 'Centrul Istoric Sinaia', 'Centrul istoric al orașului Sinaia este un loc deosebit de atrăgător pentru iubitorii de arhitectură veche și istorie. Aici se găsesc clădiri tradiționale, străzi înguste și pietruite, precum și multe magazine și restaurante care reflectă tradițiile locale. Zona este un amestec între stilul arhitectural al caselor vechi și modernitatea orașului, fiind ideală pentru o plimbare de descoperire. Vizitatorii pot explora locații istorice, cum ar fi vechile vile și construcțiile care au marcat dezvoltarea turismului în Sinaia la sfârșitul secolului XIX.', 'sina5.jpg'),
(13, 'Pârtia de Schi Straja', 'Pârtia de Schi Straja este una dintre cele mai importante atracții de iarnă din zona Munților Vâlcan. Cu pârtii variate pentru toate nivelurile de dificultate, Straja este locul ideal pentru iubitorii de schi și snowboard. Stațiunea este dotată cu telescaun, teleschi și instalații moderne, iar peisajul montan face experiența și mai plăcută. Straja este deschisă pe parcursul întregului sezon de iarnă și este potrivită atât pentru începători, cât și pentru schiori avansați. Este locul perfect pentru o vacanță activă în natură.', 'stra1.jpg'),
(13, 'Lacul Surduc', 'Lacul Surduc este o destinație de relaxare situată în apropiere de Straja, unde turiștii pot petrece timp în aer liber. Lacul este cunoscut pentru peisajele pitorești și liniștea oferită de zona înconjurătoare. Este un loc perfect pentru picnicuri, plimbări cu barca sau pescuit. De asemenea, zona din jurul lacului oferă un cadru natural ideal pentru drumeții și plimbări în natură. Este o destinație deosebită pentru cei care doresc să se deconecteze și să petreacă timp în mijlocul naturii.', 'stra2.jpg'),
(13, 'Vârful Straja', 'Vârful Straja este un loc deosebit pentru iubitorii de drumeție și panorame montane. Situat la altitudinea de 1.440 m, vârful oferă o priveliște uimitoare asupra împrejurimilor. Traseele de drumeție sunt ușor accesibile și potrivite pentru toate vârstele. De la vârf, turiștii pot vedea întregul masiv Vâlcan și o mare parte din județul Hunedoara. Vârful Straja este locul perfect pentru o excursie în natură, iar peisajul de la înălțime face această destinație un loc de neuitat.', 'stra3.jpg'),
(13, 'Stațiunea Straja', 'Stațiunea Straja, situată în apropierea orașului Lupeni, este o destinație montană ideală pentru iubitorii de aer curat și natură. Acesta este locul perfect pentru vacanțele de iarnă, dar și pentru relaxare în timpul verii, oferind o gamă largă de activități de agrement. Stațiunea are facilități moderne, inclusiv pârtii de schi, restaurante, cafenele și locuri de cazare, iar peisajele montane sunt deosebite în orice sezon. Straja atrage turiști din toate colțurile țării, fiind un loc ideal pentru o vacanță activă sau de relaxare.', 'stra4.jpg'),
(13, 'Biserica Straja', 'Biserica Straja este un loc de cult local cu o istorie adâncă și o arhitectură simplă, dar impresionantă. Situată în centrul stațiunii, biserica este un loc de pelerinaj pentru locuitorii din zonă, dar și pentru turiști. Lăcașul de cult este cunoscut pentru atmosfera sa liniștită și pentru peisajele montane care îl înconjoară. Biserica reprezintă o destinație spirituală importantă și este un loc unde vizitatorii pot găsi atât liniște interioară, cât și o conexiune profundă cu tradițiile locale.', 'stra5.jpg'),
(14, 'Parcul Central Vatra Dornei', 'Parcul Central din Vatra Dornei este un loc emblematic al orașului, ideal pentru plimbări și relaxare în aer liber. Încadrat de clădiri istorice și fântâni arteziene, parcul are alei largi, arbori seculari și o atmosferă calmă. Aici se află și celebra statuie a lui Mihai Eminescu, dar și izvoare cu apă minerală. Este un spațiu preferat atât de localnici, cât și de turiști, mai ales pentru liniștea oferită și frumusețea peisajelor. Parcul găzduiește frecvent evenimente culturale și târguri tradiționale.', 'vat1.jpg'),
(14, 'Pârtia de Schi Dealul Negru', 'Pârtia Dealul Negru din Vatra Dornei este una dintre cele mai lungi pârtii din România, având peste 3 km lungime. Aceasta este destinată schiorilor cu experiență, dar există și variante pentru începători. Pârtia este dotată cu telescaun și oferă o panoramă spectaculoasă asupra orașului și a munților din jur. Iarna, stațiunea atrage un număr mare de turiști datorită zăpezii consistente și atmosferei autentice de vacanță. Este o destinație ideală pentru pasionații de sporturi de iarnă.', 'vat2.jpg'),
(14, 'Cazinoul Băilor', 'Cazinoul Băilor din Vatra Dornei este o clădire impresionantă, construită în stil eclectic la începutul secolului XX. Deși se află într-un proces de restaurare, rămâne un simbol al stațiunii. Aflat chiar lângă Parcul Central, cazinoul a fost odinioară loc de divertisment pentru elitele vremii. Arhitectura sa elegantă atrage turiști pasionați de istorie și cultură. După restaurare, clădirea va găzdui evenimente culturale și expoziții, păstrându-și farmecul istoric și rolul central în viața turistică a orașului.', 'vat3.jpg'),
(14, 'Izvoarele cu Apă Minerală', 'Vatra Dornei este renumită pentru izvoarele sale minerale, cu proprietăți terapeutice remarcabile. Apa este utilizată atât pentru consum intern, cât și pentru tratamente balneare, contribuind la tratarea bolilor digestive, respiratorii și reumatice. Turiștii pot vizita pavilioanele din oraș unde se pot hidrata direct de la izvoare sau pot accesa centrele de tratament. Aceste izvoare au fost exploatate încă din secolul XIX și rămân o componentă esențială a stațiunii balneare.', 'vat4.jpg'),
(14, 'Muzeul de Științele Naturii', 'Muzeul de Științele Naturii din Vatra Dornei este un loc educativ și interesant pentru întreaga familie. Acesta găzduiește o colecție bogată de exponate din flora și fauna locală, cu focus pe ecosistemele montane ale Bucovinei. Muzeul oferă informații despre pădurile din zonă, fauna specifică și importanța protejării mediului. Este un loc perfect pentru cei pasionați de natură, dar și pentru copii care vor să învețe într-un mod interactiv despre biodiversitate.', 'vat5.jpg'),
(15, 'Lacul Vidra', 'Lacul Vidra este un lac de acumulare situat în inima Munților Lotrului, la o altitudine de peste 1.200 de metri. Acesta impresionează prin culoarea sa verde-albăstruie, reflectând pădurile dese care îl înconjoară. Este un loc ideal pentru relaxare, pescuit sau picnicuri liniștite în natură. Pe timpul verii, traseele montane din jur oferă peisaje spectaculoase, iar iarna, apropierea de Transalpina Ski Resort îl transformă într-un punct de plecare excelent pentru sporturile de iarnă.', 'voin1.jpg'),
(15, 'Transalpina Ski Resort',  'Situată în apropierea lacului Vidra, stațiunea Transalpina Ski Resort este una dintre cele mai moderne destinații pentru sporturi de iarnă din România. Dispune de pârtii pentru toate nivelurile de experiență, instalații de telescaun și un peisaj montan de vis. Altitudinea ridicată asigură zăpadă de calitate, iar peisajele oferă o panoramă spectaculoasă asupra Munților Parâng și Lotru. Vara, zona se transformă într-un loc perfect pentru drumeții, ciclism montan și aer curat. Stațiunea este apreciată pentru infrastructura sa modernă și accesibilitatea bună.', 'voin2.jpg'),
(15, 'Vârful Șteflești', 'Vârful Șteflești, situat în Munții Lotrului, este o destinație preferată de pasionații de drumeții montane și fotografie. Traseul către vârf traversează păduri dese, poieni alpine și creste stâncoase, oferind o varietate de peisaje și un aer curat revigorant. Altitudinea oferă o priveliște panoramică asupra întregii regiuni, iar în zilele senine se pot observa masive montane îndepărtate. Nu este un traseu dificil, fiind accesibil și turiștilor cu experiență medie. Este locul perfect pentru o aventură montană autentică în inima naturii.', 'voin3.jpg'),
(15, 'Cascada Apa Spânzurată', 'Ascunsă într-un colț de pădure de lângă Voineasa, Cascada Apa Spânzurată este un loc magic, mai puțin cunoscut, dar cu un farmec aparte. Apele sale curg peste o stâncă abruptă, creând o perdea cristalină care, în lumina soarelui, capătă reflexii spectaculoase. Accesul se face pe o potecă de pădure, ușor de parcurs pentru majoritatea vizitatorilor. Zona este sălbatică și liniștită, perfectă pentru o ieșire de o zi în natură, departe de agitația urbană. Legendele locale spun că numele cascadei provine de la forma firului de apă.', 'voin4.jpg'),
(15, 'Pădurea de Molid de la Obârșia Lotrului', 'Această pădure seculară de molid, aflată în apropiere de Obârșia Lotrului, reprezintă un ecosistem natural de o rară frumusețe. Arborii înalți și drepți, unii cu vârste de peste 150 de ani, creează o atmosferă de basm, perfectă pentru relaxare, drumeții și fotografie. Aerul curat, bogat în ozon, contribuie la o stare generală de bine. Pădurea este protejată și este un habitat important pentru numeroase specii de păsări și animale sălbatice. Este un loc ideal pentru iubitorii de natură autentică și liniște deplină.', 'voin5.jpg');
INSERT INTO hoteluri_statm (statm_id, nume, adresa, numar_telefon, stele, website, descriere_lunga, facilitati, camere, pret, imagine) VALUES
(1, 'Cabana Măgura Verde', 'Sat Galbena, Arieșeni, Alba', '+40753111444', 2, 'https://maguraverde.ro','Cabana Măgura Verde este un refugiu rustic situat la marginea pădurii, perfect pentru cei ce caută liniște și aer curat. Cabana dispune de un foișor cu zonă de grătar, camere simple dar curate și bucătărie comună. Acces direct la trasee montane și posibilitate de campare în curte.','grătar, bucătărie comună, parcare, curte mare, Wi-Fi în zona comună', 'camere duble, camere cu baie comună, căsuțe de lemn', 140, 'harieseni1.jpg'),
(1, 'Vila Piatra Albă', 'Str. Muntele Mare, nr. 9, Arieșeni', '+40753111666', 4, 'https://piatraalba.ro','Vila Piatra Albă este o locație elegantă, ideală pentru vacanțe relaxante în mijlocul naturii. Dispune de camere moderne, sală de evenimente, lounge cu șemineu și restaurant propriu. Pe timp de iarnă, se organizează tururi cu sania trasă de cai, iar vara drumeții montane.','restaurant, lounge, șemineu, organizare evenimente, parcare, Wi-Fi', 'camere deluxe, camere duble, apartamente spațioase', 400.00, 'harieseni2.jpg'),
(1, 'Pensiunea Cetina', 'Sat Avram Iancu, nr. 88, Arieșeni', '+40753111888', 2, 'https://www.turistinfo.ro/arieseni/cazare-arieseni/pensiunea_cristina-c95526.html','Pensiunea Cetina este o alegere prietenoasă cu bugetul, potrivită pentru familii și drumeți. Oferă cazare simplă, dar curată, într-un mediu liniștit. Dispune de bucătărie comună, curte largă și zonă de picnic. Gospodarii oferă și tururi către peșteri și chei locale. Atmosfera este autentică și ospitalieră.','bucătărie comună, foișor, zonă picnic, tururi ghidate, parcare, Wi-Fi', 'camere duble, camere de familie', 150, 'harieseni3.jpg'),
(1, 'Hotel Apuseni Wild', 'Str. Principală, nr. 142, Arieșeni', '+40753111999', 4, 'https://apuseniwild.com/','Hotel Apuseni Wild este o locație nouă și modernă, cu dotări premium. Camerele sunt spațioase, cu balcoane și vedere la munte. Hotelul dispune de restaurant rafinat, zonă de wellness cu saună și ciubăr și sală de conferințe. Este ideal pentru vacanțe de relaxare sau evenimente corporate în natură.','restaurant, wellness (saună, ciubăr), sală conferințe, parcare, Wi-Fi','camere premium, camere duble, apartamente deluxe', 490, 'harieseni4.jpg'),
(1, 'Vila Lupilor', 'Str. Lupului, nr. 5, Arieșeni', '+40753112000', 3, 'https://www.rentbyowner.com/property/cabana-lupilor/BC-10921647','Vila Lupilor se află într-o zonă retrasă, înconjurată de păduri. Este o locație de tip boutique, cu design modern combinat cu elemente tradiționale. Se oferă activități în aer liber, precum foc de tabără și plimbări cu sania. Camerele sunt elegante, iar gazdele foarte primitoare.','Wi-Fi, parcare, grădină, foișor, activități montane, foc de tabără', 'camere duble, camere king, apartamente cu living', 310, 'harieseni5.jpg'),
(2, 'Vila Alpin Azuga', 'Str. Teleferic, nr. 10, Azuga, Prahova', '+40760222111', 3, 'https://www.booking.com/hotel/ro/alpin-lodge.ro.html?msockid=004655f3946e61f00c5341a495b760b8','Vila Alpin Azuga este amplasată la câțiva pași de stația de telecabină, fiind o alegere excelentă pentru pasionații de schi. Camerele sunt confortabile și decorate în stil montan, iar curtea interioară este amenajată cu foișor și zonă de grătar. Gazdele oferă micul dejun inclus și informații despre trasee locale.', 'Wi-Fi, parcare, zonă BBQ, foișor, mic dejun, depozit schiuri', 'camere duble, camere cu balcon, apartamente cu chicinetă', 220, 'hazuga1.jpg'),
(2, 'Pensiunea Noni Azuga', 'Str. Ritivoiu nr. 18, Azuga 105100, Prahova', '+40760222333', 3, 'https://pensiuneanoni.ro','Pensiunea Noni este o locație liniștită, situată în apropiere de centrul stațiunii. Camerele sunt simple și confortabile, iar atmosfera este caldă și prietenoasă. Aproape de telecabină și trasee montane. Mic dejun inclus și parcare disponibilă.', 'mic dejun, parcare, Wi-Fi, camere cu vedere, restaurant', 'camere duble, camere triple, apartamente familiale', 240.00, 'hazuga2.jpg'),
(2, 'Vila Simi Azuga', 'Str. Soarelui nr. 7, Azuga 105100, Prahova', '+40760222777', 3, 'https://www.booking.com/hotel/ro/vila-azuga-azuga.ro.html?msockid=004655f3946e61f00c5341a495b760b8','Vila Simi este o unitate modernă cu design boutique. Camerele sunt decorate elegant și dispun de balcoane cu vedere. Se oferă mic dejun tradițional, iar oaspeții pot închiria biciclete pentru plimbări pe Valea Azugii. Loc ideal pentru relaxare activă.','mic dejun, Wi-Fi, biciclete, parcare, camere cu balcon', 'camere king, camere twin, apartamente', 270.00, 'hazuga3.jpg'),
(2, 'Vila Floare de Colț Azuga', 'Str. Valea Azugii nr. 18, Azuga 105100, Prahova', '+40760222999', 3, 'https://www.booking.com/hotel/ro/vila-floare-de-colt-sinaia.fr.html','Vila Floare de Colț se află într-o zonă liniștită, la marginea pădurii. Dispune de camere cu mobilier din lemn natural, foișor, grătar și mic dejun inclus. Locația este ideală pentru familii și cupluri care caută natură și liniște.','mic dejun, foișor, grătar, Wi-Fi, parcare', 'camere duble, camere de familie', 190, 'hazuga4.jpg'),
(3, 'Hotel Nord', 'Str. Alexandru Ioan Cuza nr. 237A, Borșa, Maramureș', '+40740222111', 4, 'https://nordhotelborsa.ro', 'Hotel Nord oferă servicii de cazare premium în Borșa, fiind dotat cu spa, restaurant tradițional și camere elegante. Situat aproape de centrul stațiunii, este ideal pentru familii, turiști și iubitori ai sporturilor de iarnă. Priveliștile spre munți sunt spectaculoase, iar personalul este mereu amabil și prompt.', 'spa, restaurant, parcare, Wi-Fi, recepție 24/7, mic dejun inclus', 'camere duble, camere deluxe, apartamente', 470.00, 'hborsa1.jpg'),
(3, 'Hotel Victoria', 'Str. Strâmtura nr. 9, Borșa, Maramureș', '+40740222222', 3, 'https://borsa-cazare.ro', 'Hotel Victoria este o alegere populară în Borșa, oferind camere spațioase, restaurant și acces facil la stația de telescaun. Amplasat într-o zonă liniștită, este ideal pentru relaxare în orice sezon. Dispune de sală de evenimente și parcare privată.', 'restaurant, sală de evenimente, parcare, Wi-Fi, recepție, încălzire centrală', 'camere duble, camere triple, apartamente', 320.00, 'hborsa2.jpg'),
(3, 'Hotel Cerbul', 'Stațiunea Borșa, Maramureș', '+40740222333', 3, 'https://www.borsacazare.ro', 'Hotel Cerbul este situat în zona centrală a stațiunii Borșa, aproape de pârtii și atracții naturale. Oferă camere confortabile, restaurant cu preparate maramureșene și un lounge spațios. Ideal pentru turiști care caută cazare accesibilă și servicii bune.', 'restaurant, lounge, Wi-Fi, bar, parcare, sală mese', 'camere duble, camere familiale', 280.00, 'hborsa3.jpg'),
(3, 'Pensiunea Cascada Cailor', 'Borșa, Maramureș', '+40740222444', 4, 'https://ghidul-horeca.ro/cabana-cascada-cailor-din-borsa', 'Pensiunea Cascada Cailor este situată în apropiere de una dintre cele mai spectaculoase cascade din România. Cu vedere superbă și aer curat, oferă cazare în camere rustice dar confortabile. Ideală pentru relaxare și drumeții în Munții Rodnei.', 'Wi-Fi, parcare, grădină, terasă, zonă de relaxare', 'camere duble, camere cu balcon', 300.00, 'hborsa4.jpg'),
(3, 'Pensiunea Alexis', 'Str. Strâmtura nr. 2, Borșa, Maramureș', '+40740222555', 3, 'https://www.elsetrip.com/pensiuni/pensiunea-alexis', 'Pensiunea Alexis este o locație prietenoasă, potrivită pentru vacanțe în familie. Camerele sunt luminoase, iar curtea mare permite relaxare în natură. Aproape de trasee turistice și atracții locale, pensiunea oferă confort și ospitalitate autentică.', 'Wi-Fi, grătar, foișor, bucătărie comună, parcare', 'camere duble, camere triple, apartamente familiale', 240.00, 'hborsa5.jpg'),
(3, 'Pensiunea Rustic', 'Str. Bogdan Vodă nr. 54, Borșa, Maramureș', '+40740222666', 2, 'https://www.elsetrip.com/pensiuni/pensiunea-rustic-borsa', 'Pensiunea Rustic oferă un cadru natural autentic, cu camere tradiționale și mâncare gătită în stil maramureșean. Dispune de foișor, zonă de foc și posibilitate de drumeții. Locație potrivită pentru o evadare din cotidian.', 'restaurant, foișor, foc de tabără, Wi-Fi, parcare', 'camere duble, camere de familie', 210.00, 'hborsa6.jpg'),
(4, 'Hotel Silva', 'Str. Telecabinei nr. 54A, Bușteni, Prahova', '+40244321412', 3, 'https://www.hotelsilva.ro/', 'Hotel Silva este situat la poalele Masivului Caraiman, oferind camere confortabile cu vedere panoramică. Dispune de restaurant, săli de conferințe și este aproape de telecabină.', 'restaurant, săli conferințe, Wi-Fi, parcare', 'camere duble standard, camere duble superioare, apartamente duplex', 350.00, 'hbusteni1.jpg'),
(4, 'Hotel Alexandros', 'Bulevardul Libertății, Bușteni, Prahova', '+40729800729', 3, 'https://www.hotelalexandros.ro/', 'Hotel Alexandros oferă 43 de spații de cazare moderne, restaurant, săli de conferințe și terenuri de sport, fiind ideal pentru sejururi relaxante sau evenimente corporate.', 'restaurant, săli conferințe, terenuri sport, Wi-Fi, parcare', 'camere duble, camere triple, apartamente', 400.00, 'hbusteni2.jpg'),
